const items = $input.all();
const outputItems = [];

const socialPatterns = ['linkedin.com', 'twitter.com', 'instagram.com'];

for (const item of items) {
    const websiteText = item.json.website_text?.[0] || '';
    const allExternalLinks = item.json.links || [];

    const socialLinks = allExternalLinks.filter(link => {
        if (!link) return false; 
        return socialPatterns.some(pattern => link.toLowerCase().includes(pattern));
    });

    const socialLinksString = socialLinks.join('\n');


    const originalLeadData = {
        first_name: $('Capture Lead Data').first().json.first_name,
        last_name: $('Loop Over Leads').first().json.last_name,
        email: $('Loop Over Leads').first().json.email,
        website_url: $('Loop Over Leads').first().json.website_url,
        title: $('Loop Over Leads').first().json.title,
        company: $('Loop Over Leads').first().json.company,
        row_number: $('Loop Over Leads').first().json.row_number
    };

    const processingStatus = (websiteText && websiteText.length > 50) ? 'Success' : 'Scrape Fail';

    const newItem = {
        website_text: websiteText, 
        social_links_string: socialLinksString,
        all_lead_data: originalLeadData, 
        status: processingStatus
    };

    outputItems.push({ json: newItem });
}

return outputItems;

