const items = $input.all();
const outputItems = [];

for (const item of items) {
    const rawText = item.json.content[0].text;

    const cleanText = rawText.replace(/```json|```/g, '').trim();

    let subject = 'AI_ERROR';
    let body = 'Parsing Failed - Check Anthropic Output.';

    try {
      const parsed = JSON.parse(cleanText);

      subject = parsed?.message?.subject || subject;
      body = parsed?.message?.body || body;
    } catch (error) {
      console.error("JSON parse error:", error);
    }

    outputItems.push({
      json: {
        subject: subject,
        body: body,
        all_lead_data: $('Merge Summary').first().json.all_lead_data, 
        website_summary: $('Merge Summary').first().json.website_summary
      }
    });
}

return outputItems;
