const items = $input.all(); // Get all rows
const pad = n => String(n).padStart(2, '0');

return items.map(item => {
    const input = item.json;

    // Convert formattedDate (UTC) to JS Date
    const d = new Date(input.formattedDate);

    // Build ISO string in UTC
    const isoUTC = `${d.getUTCFullYear()}-${pad(d.getUTCMonth() + 1)}-${pad(d.getUTCDate())}T` +
                   `${pad(d.getUTCHours())}:${pad(d.getUTCMinutes())}:${pad(d.getUTCSeconds())}Z`;

    return {
        json: {
            ...input,
            formatted: isoUTC
        }
    };
});

