BEGIN
  -- Drop user if exists
  BEGIN
    EXECUTE IMMEDIATE 'DROP USER docuser CASCADE';
  EXCEPTION
    WHEN OTHERS THEN
      IF SQLCODE != -1918 THEN  -- ORA-01918: user does not exist
        RAISE;
      END IF;
  END;

  -- Drop tablespace if exists
  BEGIN
    EXECUTE IMMEDIATE 'DROP TABLESPACE tbs1 INCLUDING CONTENTS AND DATAFILES';
  EXCEPTION
    WHEN OTHERS THEN
      IF SQLCODE != -959 THEN  -- ORA-00959: tablespace does not exist
        RAISE;
      END IF;
  END;

  -- Create tablespace
  EXECUTE IMMEDIATE q'[
    CREATE TABLESPACE tbs1
      DATAFILE 'tbs1.dbf' SIZE 1G
      AUTOEXTEND ON
      EXTENT MANAGEMENT LOCAL
      SEGMENT SPACE MANAGEMENT AUTO
  ]';

  -- Create user
  EXECUTE IMMEDIATE q'[
    CREATE USER docuser IDENTIFIED BY docuser
    DEFAULT TABLESPACE tbs1
    QUOTA UNLIMITED ON tbs1
  ]';
END;

DECLARE
  v_modelDir VARCHAR2(4000) := :modelDir; -- bind variable or value
BEGIN
  EXECUTE IMMEDIATE 'GRANT DB_DEVELOPER_ROLE, CREATE CREDENTIAL TO docuser';
  EXECUTE IMMEDIATE 'CREATE OR REPLACE DIRECTORY DEMO_DIR AS ''' || v_modelDir || '''';
  EXECUTE IMMEDIATE 'GRANT READ, WRITE ON DIRECTORY DEMO_DIR TO docuser';
END;

BEGIN
  -- Drop ONNX model if exists
  BEGIN
    dbms_vector.drop_onnx_model(model_name => 'doc_model', force => TRUE);
  EXCEPTION
    WHEN OTHERS THEN
      NULL; -- Ignore if model doesn't exist
  END;

  -- Load ONNX model
  BEGIN
    DBMS_VECTOR.LOAD_ONNX_MODEL(
      'DEMO_DIR',
      'ALL_MINILM_L12_V2.onnx',
      'doc_model',
      JSON('{
        "function": "embedding",
        "embeddingOutput": "embedding",
        "input": { "input": ["DATA"] }
      }')
    );
  END;

  -- Create table if not exists
  BEGIN
    EXECUTE IMMEDIATE '
      CREATE TABLE doc_tab (
        id NUMBER,
        text VARCHAR2(500)
      )
    ';
  EXCEPTION
    WHEN OTHERS THEN
      IF SQLCODE != -955 THEN -- ORA-00955: name is already used by an existing object
        RAISE;
      END IF;
  END;

  -- Drop index if exists
  BEGIN
    EXECUTE IMMEDIATE 'DROP INDEX my_hybrid_idx';
  EXCEPTION
    WHEN OTHERS THEN
      IF SQLCODE NOT IN (-1418, -1419, -1418, -1419, -1418, -1419, -1418, -1419) THEN
        NULL; -- Ignore missing index errors
      END IF;
  END;

END;

