SELECT jt.rowid_col, jt.chunk_text, jt.score
FROM (
  SELECT JSON_SERIALIZE(
           DBMS_HYBRID_VECTOR.SEARCH(
             JSON('{
               "hybrid_index_name" : "my_hybrid_idx",
               "vector": {
                 "search_text" : "{{ $json.message}}",
                 "search_mode" : "DOCUMENT",
                 "aggregator"  : "MAX"
               },
               "return": {
                 "values" : ["rowid", "chunk_text", "score"],
                 "topN"   : 3
               }
             }')
           )
           RETURNING CLOB PRETTY
         ) AS result_json
  FROM dual
) r,
JSON_TABLE(r.result_json, '$[*]'
  COLUMNS (
    rowid_col VARCHAR2(64) PATH '$.rowid',
    chunk_text VARCHAR2(500) PATH '$.chunk_text',
    score     NUMBER       PATH '$.score'
  )
) jt

