// Input: Oracle node output where item.json.RESULT is a JSON array string
const results = [];

for (const item of items) {
  const outer = item.json.RESULT;
  if (!outer) continue;

  let inner;
  try {
    inner = JSON.parse(outer);
  } catch (err) {
    throw new Error('Failed to parse inner JSON: ' + err.message);
  }

  for (const row of inner) {
    // Extract the candidate prefix (e.g., "Candidate-2:") and the rest
    const match = row.chunk_text.match(/^(Candidate-\d+):\s*(.+)$/);
    let candidateLabel, text;

    if (match) {
      candidateLabel = match[1];
      text = match[2];
    } else {
      candidateLabel = "Candidate";
      text = row.chunk_text;
    }

    // Split first sentence as "title", rest as "summary"
    const [firstSentence, ...rest] = text.split('.');
    const title = firstSentence.trim();
    const summary = rest.join('.').trim();

    // Format for chat
    results.push({
      json: {
        message: `ðŸ‘¤ **${candidateLabel} â€” ${title}**\n${summary}`,
      },
    });
  }
}

return results;
