// Parse CSV and validate data structure
let csv = $json.data;

// Clean up bash-style input
csv = csv.replace(/^\$'/, '').replace(/'$/, '').replace(/\\n/g, '\n').trim();

const delimiter = csv.includes(';') && !csv.includes(',') ? ';' : ',';
const lines = csv.split('\n').filter(line => line.trim());

if (lines.length < 2) {
  throw new Error('CSV must contain at least a header and one data row');
}

const headers = lines[0].split(delimiter).map(h => h.trim().toLowerCase());
const requiredColumns = ['sku', 'quantity', 'customer_email', 'order_date'];

// Validate required columns
const missingColumns = requiredColumns.filter(col => !headers.includes(col));
if (missingColumns.length > 0) {
  throw new Error(`Missing required columns: ${missingColumns.join(', ')}`);
}

// Parse rows
const output = [];
const errors = [];

for (let i = 1; i < lines.length; i++) {
  const values = lines[i].split(delimiter).map(v => v.trim());
  const row = {};

  headers.forEach((header, idx) => {
    row[header] = values[idx] || null;
  });

  // Validate row
  const rowErrors = [];

  if (!row.sku) rowErrors.push('SKU is required');
  if (!row.quantity || isNaN(row.quantity) || parseInt(row.quantity) <= 0) {
    rowErrors.push('Quantity must be a positive number');
  }
  if (!row.customer_email || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(row.customer_email)) {
    rowErrors.push('Valid email is required');
  }
  if (!row.order_date || !/^\d{4}-\d{2}-\d{2}$/.test(row.order_date)) {
    rowErrors.push('Date must be in YYYY-MM-DD format');
  }

  if (rowErrors.length > 0) {
    errors.push({
      row: i,
      data: row,
      errors: rowErrors
    });
  } else {
    output.push({
      json: {
        sku: row.sku.toUpperCase(),
        quantity: parseInt(row.quantity),
        customer_email: row.customer_email.toLowerCase(),
        order_date: row.order_date,
        row_number: i
      }
    });
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed:\n${JSON.stringify(errors, null, 2)}`);
}

return output;

// Load product data from Datatable
const products = $('Load Product Catalog').all().map(p => p.json);

// Create fast SKU lookup map
const productMap = Object.fromEntries(
  products.map(p => [p.sku.toUpperCase(), p])
);

// Load parsed CSV rows explicitly from the "Parse & Validate CSV" node
const orders = $('Parse & Validate CSV').all().map(o => o.json);

const enrichedItems = [];
const missingProducts = [];

for (const order of orders) {
  const { sku, quantity, customer_email, order_date, row_number } = order;
  const product = productMap[sku.toUpperCase()];

  if (!product) {
    missingProducts.push({ sku, row: row_number });
    continue;
  }

  // Calculate totals
  const qty = Number(quantity);
  const line_total = product.price * qty;
  const tax_amount = line_total * product.tax_rate;
  const total_with_tax = line_total + tax_amount;

  enrichedItems.push({
    json: {
      sku,
      product_name: product.name,
      unit_price: product.price,
      quantity: qty,
      line_total: +line_total.toFixed(2),
      tax_rate: product.tax_rate,
      tax_amount: +tax_amount.toFixed(2),
      total_with_tax: +total_with_tax.toFixed(2),
      customer_email,
      order_date,
      row_number
    }
  });
}

// Throw if missing SKUs
if (missingProducts.length > 0) {
  throw new Error(`Missing SKUs: ${JSON.stringify(missingProducts)}`);
}

return enrichedItems;

// Group by customer and calculate invoice totals
const items = $input.all().map(item => item.json);

// Group by customer email
const invoicesByCustomer = {};

items.forEach(item => {
  const email = item.customer_email;

  if (!invoicesByCustomer[email]) {
    invoicesByCustomer[email] = {
      customer_email: email,
      order_date: item.order_date,
      line_items: [],
      subtotal: 0,
      total_tax: 0,
      grand_total: 0
    };
  }

  invoicesByCustomer[email].line_items.push({
    sku: item.sku,
    product_name: item.product_name,
    unit_price: item.unit_price,
    quantity: item.quantity,
    line_total: item.line_total,
    tax_amount: item.tax_amount
  });

  invoicesByCustomer[email].subtotal += item.line_total;
  invoicesByCustomer[email].total_tax += item.tax_amount;
  invoicesByCustomer[email].grand_total += item.total_with_tax;
});

// Generate invoice IDs and format
const invoices = Object.values(invoicesByCustomer).map((invoice, idx) => {
  const timestamp = Date.now();
  const invoiceId = `INV-${timestamp}-${idx + 1}`;

  return {
    json: {
      invoice_id: invoiceId,
      customer_email: invoice.customer_email,
      order_date: invoice.order_date,
      created_at: new Date().toISOString(),
      line_items: invoice.line_items,
      subtotal: Math.round(invoice.subtotal * 100) / 100,
      total_tax: Math.round(invoice.total_tax * 100) / 100,
      grand_total: Math.round(invoice.grand_total * 100) / 100,
      status: 'pending'
    }
  };
});

return invoices;

// Check for duplicate orders based on customer email and order date
// In production, query actual database

const invoices = $input.all().map(item => item.json);

// Simulate existing orders database
const existingOrders = [
  // Example: { customer_email: 'test@example.com', order_date: '2025-01-15' }
];

const duplicates = [];
const validInvoices = [];

for (const invoice of invoices) {
  const isDuplicate = existingOrders.some(
    order => order.customer_email === invoice.customer_email && 
             order.order_date === invoice.order_date
  );

  if (isDuplicate) {
    duplicates.push({
      invoice_id: invoice.invoice_id,
      customer_email: invoice.customer_email,
      order_date: invoice.order_date,
      reason: 'Duplicate order for this customer and date'
    });
  } else {
    validInvoices.push({ json: invoice });
  }
}

if (duplicates.length > 0) {
  // Store duplicates in global variable for error reporting
  $('Globals').item.json.duplicates = duplicates;
}

return validInvoices.length > 0 ? validInvoices : [{ json: { no_valid_invoices: true, duplicates } }];

// Each input item has a structure like { data: [ {...}, {...} ] }
// We need to flatten all data arrays into a single list of invoice objects
const allData = $input.all()
  .flatMap(item => item.json.data || [])
  .filter(Boolean);

if (allData.length === 0) {
  throw new Error('No invoice data found.');
}

const emailData = allData.map(invoice => {
  // No line_items in Datatable version ‚Üí create a simple item summary
  const body = `Dear Customer,

Thank you for your order!

Invoice ID: ${invoice.invoice_id}
Order Date: ${new Date(invoice.order_date).toLocaleDateString()}

Subtotal: $${invoice.subtotal.toFixed(2)}
Tax: $${invoice.total_tax.toFixed(2)}
Grand Total: $${invoice.grand_total.toFixed(2)}

Thank you for your business!

Best regards,
Sales Team`;

  return {
    json: {
      to: invoice.customer_email,
      subject: `Invoice ${invoice.invoice_id} - Order Confirmation`,
      body,
      invoice_id: invoice.invoice_id,
      metadata: invoice
    }
  };
});

return emailData;

// Merge invoice data with email results
const invoices = $input.first().json;
const emailResults = $input.all().slice(1).map(item => item.json);

return {
  success: true,
  processed_at: new Date().toISOString(),
  invoice_count: Array.isArray(invoices) ? invoices.length : 1,
  invoices: invoices,
  email_notifications: emailResults,
  message: 'All invoices processed and customers notified'
};
