const aiResponse = JSON.parse($input.first().json.choices[0].message.content);
const emailData = $('Monitor Support Emails').first().json;

return {
  json: {
    timestamp: new Date().toISOString(),
    customer_email: emailData.from,
    customer_name: emailData.from.split('<')[0].trim(),
    subject: emailData.subject,
    body: emailData.body,
    email_id: emailData.id,
    thread_id: emailData.threadId,

    // AI Analysis Results
    sentiment: aiResponse.sentiment,
    urgency: aiResponse.urgency_level,
    category: aiResponse.category,
    key_issues: aiResponse.key_issues,
    suggested_response: aiResponse.suggested_response,

    // Scoring
    priority_score: calculatePriority(aiResponse.urgency_level, aiResponse.sentiment),
    requires_immediate_attention: aiResponse.urgency_level === 'Critical' || aiResponse.sentiment === 'Critical'
  }
};

function calculatePriority(urgency, sentiment) {
  const urgencyScores = { 'Low': 1, 'Medium': 2, 'High': 3, 'Critical': 4 };
  const sentimentScores = { 'Positive': 0, 'Neutral': 1, 'Negative': 2, 'Critical': 3 };
  return (urgencyScores[urgency] || 2) * 25 + (sentimentScores[sentiment] || 1) * 10;
}

