// Spam detection keywords
const spamKeywords = ['buy now', 'click here', 'dm me', 'check bio', 'follow for follow', 'f4f', 'l4l', 'spam', 'bot'];

const commentText = $input.item.json.text.toLowerCase();

// Check for spam
const isSpam = spamKeywords.some(keyword => commentText.includes(keyword));

// Check if comment is too short (likely not genuine)
const isTooShort = commentText.length < 3;

// Check if comment is just emojis
const isOnlyEmojis = /^[\p{Emoji}\s]+$/u.test(commentText);

return {
  json: {
    ...($input.item.json),
    isSpam: isSpam,
    isTooShort: isTooShort,
    isOnlyEmojis: isOnlyEmojis,
    shouldReply: !isSpam && !isTooShort && !isOnlyEmojis
  }
};

