const assignments = $input.all();
const numPeers = 3;
const results = [];

for (let i = 0; i < assignments.length; i++) {
  const assignment = assignments[i].json;
  const reviewers = [];

  for (let j = 1; j <= numPeers; j++) {
    const reviewerIndex = (i + j) % assignments.length;
    reviewers.push({
      reviewerId: assignments[reviewerIndex].json.studentId,
      reviewerName: assignments[reviewerIndex].json.studentName,
      reviewerEmail: assignments[reviewerIndex].json.email
    });
  }

  results.push({
    assignmentId: assignment.assignmentId,
    studentId: assignment.studentId,
    studentName: assignment.studentName,
    submissionUrl: assignment.submissionUrl,
    assignmentTitle: assignment.assignmentTitle,
    reviewers: reviewers,
    dueDate: assignment.dueDate
  });
}

return results;

