const reviewData = $input.item.json;
const scores = {
  technicalAccuracy: Math.floor(Math.random() * 26),
  problemSolving: Math.floor(Math.random() * 26),
  documentation: Math.floor(Math.random() * 21),
  codeQuality: Math.floor(Math.random() * 21),
  innovation: Math.floor(Math.random() * 11)
};

const totalScore = Object.values(scores).reduce((a, b) => a + b, 0);
const grade = totalScore >= 90 ? 'A' : totalScore >= 80 ? 'B' : totalScore >= 70 ? 'C' : totalScore >= 60 ? 'D' : 'F';

return {
  ...reviewData,
  scores,
  totalScore,
  grade,
  evaluatedAt: new Date().toISOString()
};

const reviews = $input.all();
const assignmentGroups = {};

reviews.forEach(review => {
  const aid = review.json.assignmentId;
  if (!assignmentGroups[aid]) {
    assignmentGroups[aid] = [];
  }
  assignmentGroups[aid].push(review.json);
});

const results = [];
for (const [assignmentId, reviewList] of Object.entries(assignmentGroups)) {
  const expectedReviews = 3;
  const completedReviews = reviewList.length;
  const isComplete = completedReviews >= expectedReviews;

  if (isComplete) {
    const avgScore = reviewList.reduce((sum, r) => sum + r.totalScore, 0) / completedReviews;
    results.push({
      assignmentId,
      studentId: reviewList[0].studentId,
      completedReviews,
      averageScore: Math.round(avgScore * 10) / 10,
      finalGrade: avgScore >= 90 ? 'A' : avgScore >= 80 ? 'B' : avgScore >= 70 ? 'C' : avgScore >= 60 ? 'D' : 'F',
      isComplete,
      completedAt: new Date().toISOString()
    });
  }
}

return results;

const completedReviews = $input.all();
const totalReviews = completedReviews.length;
const avgScore = completedReviews.reduce((sum, r) => sum + r.json.averageScore, 0) / totalReviews;
const gradeDistribution = {};

completedReviews.forEach(review => {
  const grade = review.json.finalGrade;
  gradeDistribution[grade] = (gradeDistribution[grade] || 0) + 1;
});

return [{
  totalAssignments: totalReviews,
  classAverage: Math.round(avgScore * 10) / 10,
  gradeDistribution,
  generatedAt: new Date().toISOString()
}];

