// Extract and format the AI agent's response
const items = $input.all();

const processedResults = items.map((item, index) => {
  const agentResponse = item.json;

  return {
    json: {
      timestamp: new Date().toISOString(),
      itemIndex: index,
      agentOutput: agentResponse.output || agentResponse.text || '',
      fullResponse: agentResponse,
      status: 'processed'
    }
  };
});

return processedResults;

function cleanAndExtractJSON(response) {
    try {
        const result = {
            image_prompt: []
        };

        const lines = response.split('\n');
        let currentPrompt = '';

        for (const line of lines) {
            if (line.includes('"prompt":')) {
                if (currentPrompt) {
                    result.image_prompt.push(currentPrompt.trim());
                }
                currentPrompt = line.split('"prompt":')[1].trim();
            }
        }

        if (currentPrompt) {
            result.image_prompt.push(currentPrompt.trim());
        }

        return { json: result };

    } catch (error) {
        return { 
            json: {
                image_prompt: []
            }
        };
    }
}

const response = $input.first().json.output;
return cleanAndExtractJSON(response);

return $input.first().json.image_prompt.map(prompt => ({
  json: {
    body: {
      prompt: prompt,
  "image_size": {
    "width": $('Fields - Set Values').first().json.width,
    "height": $('Fields - Set Values').first().json.height
  },
  "num_inference_steps": 12,
  "guidance_scale": 3.5,
  "num_images": 1,
  "enable_safety_checker": true,
}
    }
  }
));

for (let i = 0; i < items.length; i++) {
  items[i].json.fileName = `images_${(i + 1).toString().padStart(3, '0')}.png`;
}
return items;
