// Define your events here
const events = [
 {
 name: "Product Launch",
 date: "2025-12-25",
 type: "launch",
 channel: "slack",
 webhookUrl: "https://hooks.slack.com/services/YOUR/WEBHOOK/URL"
 },
 {
 name: "John's Birthday",
 date: "2025-11-15",
 type: "birthday",
 channel: "email",
 emailRecipients: ["user@example.com"]
 },
 {
 name: "Conference Date",
 date: "2025-12-01",
 type: "other",
 channel: "slack",
 webhookUrl: "https://hooks.slack.com/services/YOUR/WEBHOOK/URL"
 }
];

// Calculate days remaining for each event
const today = new Date();
today.setHours(0, 0, 0, 0);

const processedEvents = events.map(event => {
 const eventDate = new Date(event.date);
 eventDate.setHours(0, 0, 0, 0);

 const diffTime = eventDate - today;
 const daysRemaining = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

 return {
 ...event,
 daysRemaining: daysRemaining,
 isPast: daysRemaining < 0,
 isToday: daysRemaining === 0,
 shouldNotify: daysRemaining >= 0 && (daysRemaining <= 7 || daysRemaining === 30 || daysRemaining === 14)
 };
});

// Filter only events that need notification
const eventsToNotify = processedEvents.filter(event => event.shouldNotify);

return eventsToNotify.map(event => ({ json: event }));

const item = $input.first().json;

// Determine emoji based on event type
let emoji = 'Calendar';
if (item.type === 'birthday') emoji = 'Birthday Cake';
if (item.type === 'launch') emoji = 'Rocket';

// Format message based on days remaining
let message = '';
if (item.isToday) {
 message = `${emoji} *TODAY IS THE DAY!* ${emoji}\n\n*${item.name}* is happening today!`;
} else if (item.daysRemaining === 1) {
 message = `${emoji} *TOMORROW!* ${emoji}\n\n*${item.name}* is happening tomorrow!`;
} else {
 message = `${emoji} *Countdown Alert* ${emoji}\n\n*${item.name}* is coming up in *${item.daysRemaining} days*!\n\nDate: ${item.date}`;
}

return {
 json: {
 text: message,
 webhookUrl: item.webhookUrl
 }
};

const item = $input.first().json;

// Determine emoji and color based on event type and urgency
let emoji = 'Calendar';
let color = '#4CAF50';
if (item.type === 'birthday') {
 emoji = 'Birthday Cake';
 color = '#FF6B6B';
}
if (item.type === 'launch') {
 emoji = 'Rocket';
 color = '#2196F3';
}

if (item.daysRemaining <= 3) color = '#FF5722';

// Format email content
let subject = '';
let bodyText = '';

if (item.isToday) {
 subject = `${emoji} TODAY: ${item.name}`;
 bodyText = `Today is the day for <strong>${item.name}</strong>!`;
} else if (item.daysRemaining === 1) {
 subject = `${emoji} TOMORROW: ${item.name}`;
 bodyText = `<strong>${item.name}</strong> is happening tomorrow!`;
} else {
 subject = `${emoji} ${item.daysRemaining} Days Until ${item.name}`;
 bodyText = `<strong>${item.name}</strong> is coming up in <strong>${item.daysRemaining} days</strong>!`;
}

const htmlBody = `
<!DOCTYPE html>
<html>
<head>
 <style>
 body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
 .container { max-width: 600px; margin: 0 auto; padding: 20px; }
 .header { background: ${color}; color: white; padding: 20px; text-align: center; border-radius: 8px 8px 0 0; }
 .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 8px 8px; }
 .countdown { font-size: 48px; font-weight: bold; color: ${color}; text-align: center; margin: 20px 0; }
 .details { background: white; padding: 15px; border-left: 4px solid ${color}; margin: 20px 0; }
 </style>
</head>
<body>
 <div class="container">
 <div class="header">
 <h1>${emoji} Event Countdown</h1>
 </div>
 <div class="content">
 <p>${bodyText}</p>
 <div class="countdown">${item.daysRemaining > 0 ? item.daysRemaining : '0'}</div>
 <p style="text-align: center; color: #666;">days remaining</p>
 <div class="details">
 <p><strong>Event:</strong> ${item.name}</p>
 <p><strong>Date:</strong> ${item.date}</p>
 <p><strong>Type:</strong> ${item.type}</p>
 </div>
 </div>
 </div>
</body>
</html>
`;

return {
 json: {
 subject: subject,
 html: htmlBody,
 recipients: item.emailRecipients
 }
};

// Process incoming webhook data
const incomingData = $input.first().json.body;

const response = {
 status: "success",
 message: "Webhook received and processed",
 timestamp: new Date().toISOString(),
 data: incomingData
};

return { json: response };
