return items.map(item => {
  let aiResponse = "";

  const json = item.json;
  if (json.message?.[0]?.content) {
    aiResponse = json.message[0].content;
  } else if (json.data) {
    aiResponse = json.data;
  } else if (json.choices?.[0]?.message?.content) {
    aiResponse = json.choices[0].message.content;
  } else if (json.text) {
    aiResponse = json.text;
  }

  aiResponse = aiResponse?.trim().toLowerCase() || "";

  const isUnsubscribe =
    aiResponse.includes("unsubscribe") ||
    aiResponse.includes("remove me") ||
    aiResponse.includes("opt out") ||
    aiResponse.includes("stop emailing") ||
    aiResponse.includes("do not contact");

  return {
    json: {
      ...json,
      aiResponse,
      isUnsubscribe
    }
  };
});

//----------------------------------------------------
// Inputs
//----------------------------------------------------
const unsubscribed = $items("Get unsubscribe sheet").map(i => 
  (i.json.Email || "").toLowerCase().trim()
);
const currentEmail = $('Watch New replies').item.json.From
  .match(/<([^>]+)>/)?.[1]?.toLowerCase().trim() || 
  $('Watch New replies').item.json.From.toLowerCase().trim();

//----------------------------------------------------
// Check and decide
//----------------------------------------------------
if (!unsubscribed.includes(currentEmail)) {
  return [{ json: { Email: currentEmail}}];
} else {
  return []; // Skip if already in the sheet
}
