// Inventory Data Normalization & Comparison Engine
// Standardizes data from all platforms and identifies discrepancies

const inputData = $input.all();

// Initialize storage
const inventoryByPlatform = {
  shopify: [],
  woocommerce: [],
  magento: [],
  erpnext: [],
  customdb: []
};

// Parse and normalize data from each platform
for (const item of inputData) {
  const json = item.json;

  // Shopify data
  if (json.products) {
    for (const product of json.products) {
      for (const variant of product.variants || []) {
        inventoryByPlatform.shopify.push({
          sku: variant.sku || variant.id.toString(),
          quantity: variant.inventory_quantity || 0,
          productName: product.title,
          variantId: variant.id,
          platform: 'shopify',
          lastUpdated: variant.updated_at
        });
      }
    }
  }

  // WooCommerce data
  if (json.id && json.sku && json.stock_quantity !== undefined) {
    inventoryByPlatform.woocommerce.push({
      sku: json.sku,
      quantity: parseInt(json.stock_quantity) || 0,
      productName: json.name,
      productId: json.id,
      platform: 'woocommerce',
      lastUpdated: json.date_modified
    });
  }

  // Magento data
  if (json.items) {
    for (const product of json.items) {
      if (product.extension_attributes?.stock_item) {
        inventoryByPlatform.magento.push({
          sku: product.sku,
          quantity: parseInt(product.extension_attributes.stock_item.qty) || 0,
          productName: product.name,
          productId: product.id,
          platform: 'magento',
          lastUpdated: product.updated_at
        });
      }
    }
  }

  // ERPNext data (Master source)
  if (json.data) {
    for (const item of json.data) {
      inventoryByPlatform.erpnext.push({
        sku: item.item_code,
        quantity: parseInt(item.stock_qty) || 0,
        productName: item.name,
        platform: 'erpnext',
        isMaster: true,
        lastUpdated: item.modified
      });
    }
  }

  // Custom Database data
  if (json.sku && json.quantity !== undefined) {
    inventoryByPlatform.customdb.push({
      sku: json.sku,
      quantity: parseInt(json.quantity) || 0,
      warehouseLocation: json.warehouse_location,
      platform: 'customdb',
      lastUpdated: json.last_updated
    });
  }
}

// Consolidate all SKUs
const allSKUs = new Set();
for (const platform in inventoryByPlatform) {
  for (const item of inventoryByPlatform[platform]) {
    if (item.sku) allSKUs.add(item.sku);
  }
}

// Compare inventory across platforms for each SKU
const discrepancies = [];
const consistentItems = [];

for (const sku of allSKUs) {
  const skuData = {
    sku: sku,
    platforms: {}
  };

  // Collect data from each platform
  for (const platform in inventoryByPlatform) {
    const platformItem = inventoryByPlatform[platform].find(item => item.sku === sku);
    if (platformItem) {
      skuData.platforms[platform] = {
        quantity: platformItem.quantity,
        lastUpdated: platformItem.lastUpdated,
        productName: platformItem.productName,
        isMaster: platformItem.isMaster || false,
        productId: platformItem.productId,
        variantId: platformItem.variantId
      };
    }
  }

  // Calculate statistics
  const quantities = Object.values(skuData.platforms).map(p => p.quantity);
  const avgQuantity = quantities.reduce((a, b) => a + b, 0) / quantities.length;
  const maxQuantity = Math.max(...quantities);
  const minQuantity = Math.min(...quantities);
  const variance = maxQuantity - minQuantity;
  const variancePercent = avgQuantity > 0 ? (variance / avgQuantity) * 100 : 0;

  skuData.statistics = {
    average: avgQuantity,
    max: maxQuantity,
    min: minQuantity,
    variance: variance,
    variancePercent: variancePercent.toFixed(2),
    platformCount: Object.keys(skuData.platforms).length
  };

  // Determine if there's a discrepancy
  if (variance > 0 && variancePercent > 5) {
    // Significant discrepancy found
    skuData.hasDiscrepancy = true;
    skuData.severity = variancePercent > 20 ? 'critical' : variancePercent > 10 ? 'warning' : 'info';
    skuData.recommendedAction = 'ai_review';

    // Use ERPNext as source of truth if available
    if (skuData.platforms.erpnext) {
      skuData.sourceOfTruth = 'erpnext';
      skuData.correctQuantity = skuData.platforms.erpnext.quantity;
      skuData.recommendedAction = 'sync_to_erpnext';
    } else {
      // Use most recently updated platform
      let mostRecent = null;
      let mostRecentTime = null;

      for (const [platform, data] of Object.entries(skuData.platforms)) {
        const updateTime = new Date(data.lastUpdated);
        if (!mostRecentTime || updateTime > mostRecentTime) {
          mostRecentTime = updateTime;
          mostRecent = platform;
        }
      }

      skuData.sourceOfTruth = mostRecent;
      skuData.correctQuantity = skuData.platforms[mostRecent].quantity;
      skuData.recommendedAction = `sync_to_${mostRecent}`;
    }

    discrepancies.push(skuData);
  } else {
    // Inventory is consistent
    skuData.hasDiscrepancy = false;
    skuData.severity = 'none';
    consistentItems.push(skuData);
  }
}

// Sort discrepancies by severity
discrepancies.sort((a, b) => {
  const severityOrder = { critical: 0, warning: 1, info: 2 };
  return severityOrder[a.severity] - severityOrder[b.severity];
});

// Return comprehensive analysis
return [{
  json: {
    summary: {
      totalSKUs: allSKUs.size,
      discrepanciesFound: discrepancies.length,
      consistentItems: consistentItems.length,
      criticalDiscrepancies: discrepancies.filter(d => d.severity === 'critical').length,
      warningDiscrepancies: discrepancies.filter(d => d.severity === 'warning').length,
      platformsConnected: Object.keys(inventoryByPlatform).filter(p => inventoryByPlatform[p].length > 0).length,
      timestamp: new Date().toISOString()
    },
    discrepancies: discrepancies,
    consistentItems: consistentItems.slice(0, 10), // Sample of consistent items
    platformData: inventoryByPlatform
  }
}];

// Split discrepancies into individual items for AI analysis
const data = $input.first().json;
const discrepancies = data.discrepancies || [];

// Return each discrepancy as separate item for AI processing
return discrepancies.map(item => ({
  json: {
    sku: item.sku,
    platforms: item.platforms,
    statistics: item.statistics,
    severity: item.severity,
    sourceOfTruth: item.sourceOfTruth,
    correctQuantity: item.correctQuantity,
    recommendedAction: item.recommendedAction,
    originalData: item
  }
}));

// Parse AI response and prepare update actions
const item = $input.first().json;

let aiDecision;
try {
  // Extract JSON from AI response
  const responseText = item.response || item.text || JSON.stringify(item);
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiDecision = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in AI response');
  }
} catch (error) {
  console.error('Failed to parse AI response:', error);
  // Fallback to original recommendation
  aiDecision = {
    decision: 'approve',
    sourceOfTruth: item.sourceOfTruth,
    correctQuantity: item.correctQuantity,
    reasoning: 'Using original recommendation due to AI parse error',
    confidence: 'low',
    requiresManualReview: true
  };
}

// Prepare update instructions for each platform
const updates = [];

if (aiDecision.decision === 'approve' && !aiDecision.requiresManualReview) {
  for (const [platform, data] of Object.entries(item.platforms)) {
    if (platform !== aiDecision.sourceOfTruth && data.quantity !== aiDecision.correctQuantity) {
      updates.push({
        platform: platform,
        sku: item.sku,
        currentQuantity: data.quantity,
        newQuantity: aiDecision.correctQuantity,
        productId: data.productId,
        variantId: data.variantId,
        action: 'update',
        reason: aiDecision.reasoning
      });
    }
  }
}

return [{
  json: {
    sku: item.sku,
    aiDecision: aiDecision,
    updates: updates,
    originalData: item,
    timestamp: new Date().toISOString(),
    requiresManualReview: aiDecision.requiresManualReview || aiDecision.decision !== 'approve'
  }
}];

// Split updates by platform for parallel execution
const item = $input.first().json;
const updates = item.updates || [];

if (updates.length === 0) {
  return [{ json: { message: 'No updates needed', sku: item.sku } }];
}

return updates.map(update => ({
  json: {
    ...update,
    sku: item.sku,
    aiDecision: item.aiDecision
  }
}));

