// n8n Function Node - Agent Output Sanitizer

const agentOutput = $input.item.json.output; // Adjust path to your agent output

function sanitizeAgentOutput(text) {
  if (!text) return '';

  let sanitized = text;

  // Remove markdown formatting
  sanitized = sanitized.replace(/\*\*(.+?)\*\*/g, '$1'); // Bold
  sanitized = sanitized.replace(/\*(.+?)\*/g, '$1'); // Italic
  sanitized = sanitized.replace(/__(.+?)__/g, '$1'); // Underline
  sanitized = sanitized.replace(/_(.+?)_/g, '$1'); // Italic underscore
  sanitized = sanitized.replace(/~~(.+?)~~/g, '$1'); // Strikethrough

  // Remove markdown headers
  sanitized = sanitized.replace(/^#{1,6}\s+/gm, '');

  // Remove code blocks
  sanitized = sanitized.replace(/```[\s\S]*?```/g, '');
  sanitized = sanitized.replace(/`(.+?)`/g, '$1');

  // Remove markdown links but keep the text
  sanitized = sanitized.replace(/\[([^\]]+)\]\([^\)]+\)/g, '$1');

  // Sanitize URLs - remove tracking parameters
  sanitized = sanitized.replace(
    /(https?:\/\/[^\s]+)/g, 
    (url) => {
      try {
        const urlObj = new URL(url);
        // Remove common tracking parameters
        ['utm_source', 'utm_medium', 'utm_campaign', 'utm_term', 'utm_content', 
         'fbclid', 'gclid', 'ref', 'source'].forEach(param => {
          urlObj.searchParams.delete(param);
        });
        return urlObj.toString();
      } catch {
        return url;
      }
    }
  );

  // Remove extra whitespace and newlines
  sanitized = sanitized.replace(/\n{3,}/g, '\n\n');
  sanitized = sanitized.trim();

  return sanitized;
}

// Return sanitized output
return {
  json: {
    sanitized: sanitizeAgentOutput(agentOutput),
    original: agentOutput
  }
};

// Normalize incoming data structure
let data = $json;

// Handle nested arrays from widget
if (Array.isArray(data) && data.length > 0) {
  if (Array.isArray(data[0]) && data[0].length > 0) {
    data = data[0][0];  // Double nested
  } else {
    data = data[0];     // Single nested
  }
}

// Ensure we have required fields
const normalizedData = {
  action: data.action || 'sendMessage',
  sessionId: data.sessionId || '',
  route: data.route || 'general',
  chatInput: data.chatInput || '',
  metadata: data.metadata || {}
};

return normalizedData;

