// get filename from Download node
const fileName = ($('Download a file').first().json.name || '').toLowerCase();

// get extracted text from OCR node
const text = ($input.first().json.extractedText || '').toLowerCase();

let type = "Unknown";

// --- Rule-based classification ---

// Priority 1: File name clues
if (fileName.includes("passport")) type = "Passport";
else if (fileName.includes("license") || fileName.includes("dl")) type = "Driving License";
else if (fileName.includes("mortgage")) type = "Mortgage Application";
else if (fileName.includes("proof") && fileName.includes("fund")) type = "Proof of Funds Letter";
else if (fileName.includes("pay") || fileName.includes("paystub")) type = "Employee Pay Statement";
else if (fileName.includes("t4")) type = "T4 Statement";
else if (fileName.includes("electric") || fileName.includes("hydro")) type = "Electricity Bill";
else if (fileName.includes("internet") || fileName.includes("shaw")) type = "Internet Bill";
else if (fileName.includes("statement") && fileName.includes("account")) type = "Account Statement";
else if (fileName.includes("bank")) type = "Bank Statement";

// Priority 2: Text-based fallback
else if (text.includes("gross pay") || text.includes("net pay")) type = "Employee Pay Statement";
else if (text.includes("bank of montreal") || text.includes("scotiabank") || text.includes("royal bank")) type = "Bank Statement";
else if (text.includes("proof of funds")) type = "Proof of Funds Letter";
else if (text.includes("mortgage application")) type = "Mortgage Application";
else if (text.includes("driver") && text.includes("licence")) type = "Driving License";
else if (text.includes("internet") && text.includes("usage")) type = "Internet Bill";
else if (text.includes("hydro") || text.includes("electricity")) type = "Electricity Bill";
else if (text.includes("t4") && text.includes("income")) type = "T4 Statement";

return [{
  json: {
    FileName: $('Download a file').first().json.name,
    DocumentType: type,
    ExtractedText: $input.first().json.extractedText
  }
}];

// Gather all items coming from the "Done" output of the Loop Over Items node
const docs = $input.all().map(item => ({
  FileName: item.json.FileName || null,
  DocumentType: item.json.DocumentType || "Unknown",
  ExtractedText: item.json.ExtractedText || ""
}));

return [{
  json: {
    BorrowerName: "Kenneth Smith", // replace with dynamic value if available
    Documents: docs
  }
}];
