function toNumber(value) {
  if (!value || value === "-" || value === "") return 0;

  let v = String(value).replace(/\//g, ".");

  v = v.replace(/,/g, "").replace(/\s/g, "");

  const num = parseFloat(v);
  return isNaN(num) ? 0 : num;
}

