// Get current item (merged data)
const data = $input.first().json;

// Build full name
const fullName = `${data["Full Name"].first} ${data["Full Name"].last}`.trim();

// Format date
const dateObj = data["Select Workshop Date"];
const displayDate = new Date(`${dateObj.year}-${dateObj.month}-${dateObj.day}`).toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// QR Code URL
const verificationUrl = `https://your-domain.com/verify?email=${encodeURIComponent(data.Email)}&event=${encodeURIComponent(data["Select Workshop"])}`;

// Unique Certificate ID
const certificateId = `CERT-${Date.now().toString(36).toUpperCase()}-${Math.random().toString(36).substr(2, 5).toUpperCase()}`;

// Return clean data for HTML
return [
  {
    json: {
      name: fullName,
      email: data.Email,
      event: data["Select Workshop"],
      date: displayDate,
      qrCodeUrl: `https://api.qrserver.com/v1/create-qr-code/?data=${encodeURIComponent(verificationUrl)}&size=200x200&margin=10`,
      verificationUrl: verificationUrl,
      certificateId: certificateId,
      verifiedAt: new Date().toISOString(),
      emailProvider: data.details?.mx?.provider || "Unknown",
      isValid: data.valid
    }
  }
];

