// Get the list of CSMs from the previous node
const csmList = $input.all();

// Check if the table is empty
if (csmList.length === 0) {
  throw new Error("CSM assignment table is empty. Please add CSMs to the Data Table.");
}

// Sort the list to find the CSM with the lowest deal_count
// The input item looks like: { json: { csm_id: '123', deal_count: 0 }, row_id: 'abc' }
const sortedList = csmList.sort((a, b) => {
  return a.json.deal_count - b.json.deal_count;
});

// The least busy CSM is the first one in the sorted list
const leastBusyCsm = sortedList[0];

// Return the data needed for the next nodes
return {
  csm_id: leastBusyCsm.json.csm_id,
  row_id_to_update: leastBusyCsm.json.id,
  current_count: leastBusyCsm.json.deal_count
};
