// Corrected Code for the Function Node (Node 4: Parse Product Targets)
const inputItemJson = $input.item.json.data[0]; // Access the actual data object inside the 'data' array

// 1. Get the productTargets string
let config = inputItemJson.productTargets;

// 2. Clean the string to ensure valid JSON array format
//    - Remove leading "[|" (if present)
//    - Replace all stray pipes '|' (if present) with a space
//    - Replace non-standard spaces/newlines with proper JSON spacing
config = config
    .trim()
    .replace(/^\[\|/, '[') // Fix the starting "[|" to just "["
    .replace(/\|/g, '');   // Remove any remaining pipes '|'

// 3. Parse the cleaned string
const products = JSON.parse(config);

const globalConfig = inputItemJson; // Use the corrected object for global config

const results = [];
for (const product of products) {
  results.push({
    json: {
      url: product.url,
      internalId: product.internal_id,
      thresholdType: product.threshold_type,
      // Pass the *unparsed* JSON strings for subsequent nodes to parse if needed
      priceThresholds: globalConfig.priceThresholds,
      alertRecipients: globalConfig.alertRecipients,
      historicalDBTable: globalConfig.historicalDBTable
    }
  });
}

return results;

const thresholds = JSON.parse($json.priceThresholds);
const type = $json.thresholdType;

// Add the relevant threshold to the current item
$json.priceThreshold = thresholds[type] || thresholds.default;

return $input.item;

