// --- START OF CODE NODE 4 SCRIPT (Stringify Output) ---
// Mode: Run Once for All Items
// Input: The array of aggregated ad creative performance objects

const allAggregatedItems = $input.all(); // This is an array of n8n items

// Extract the .json part from each n8n item to get the actual data objects
const dataToConvert = allAggregatedItems.map(item => item.json);

// Stringify the array of data objects
// The 'null, 2' arguments pretty-print the JSON string with an indent of 2 spaces,
// which can be helpful for readability if you're inspecting it, but not strictly necessary for an LLM.
// If the LLM prefers a more compact JSON, you can omit 'null, 2'.
const jsonString = JSON.stringify(dataToConvert, null, 2);

// Output a single item containing this string
return [{ json: { all_ads_data_string: jsonString } }];
// --- END OF CODE NODE 4 SCRIPT ---

// Get the first input item (assumes NocoDB returns an array)
const tokenData = $input.first()?.json || {};

// Get current time in milliseconds
const now = new Date().getTime();

// Parse the token's end date from NocoDB
const endDate = new Date(tokenData.end_date).getTime();

// Define 3 days in milliseconds
const threeDaysInMs = 3 * 24 * 60 * 60 * 1000;

// Calculate the difference between end date and now
const timeToExpiry = endDate - now;

// Determine if the token is within 3 days of expiry and not already expired
const needsRefresh = timeToExpiry > 0 && timeToExpiry <= threeDaysInMs;

// Return both the result and debug info
return [{
  json: {
    needs_refresh: needsRefresh,
    time_to_expiry_ms: timeToExpiry,
    expires_at: tokenData.end_date,
    checked_at: new Date().toISOString(),
  }
}];

// Input from the previous node
const input = $input.first();
const tokenData = input.json || {};

// Current time in milliseconds
const now = new Date().getTime();

// Token details from input
const accessToken = tokenData.access_token || '';
const expiresIn = tokenData.expires_in || 5184000; // Default to 60 days (5184000 seconds) if not provided
const issuedAt = tokenData.issued_at ? new Date(tokenData.issued_at).getTime() : now; // Use now if issued_at is missing
const expiryTime = issuedAt + (expiresIn * 1000); // Convert expires_in to milliseconds
const endDate = new Date(expiryTime).toISOString(); // Calculated end date

// Output the end date and access token
return [{
  json: {
    access_token: accessToken,
    end_date: endDate,
    current_time: new Date().toISOString()
  }
}];

// --- START OF CODE NODE 1 SCRIPT (Process Raw Data) ---
// Mode: Run Once for All Items

const allN8NInputItems = $input.all(); // Gets all n8n items (usually just one from HTTP Request if pagination combines, or one per page)
const finalOutputItems = [];

// Iterate over each n8n item received (typically one, or one per page if HTTP node outputs that way)
for (const n8nItem of allN8NInputItems) {
    const facebookResponse = n8nItem.json; // This is an object like: { "data": [...], "paging": ... }

    // Check if the 'data' array exists in the Facebook response
    if (facebookResponse && facebookResponse.data && Array.isArray(facebookResponse.data)) {
        const adsInsightsArray = facebookResponse.data; // This is the array of actual ad insight objects

        // Loop through each ad insight object within the 'data' array
        for (const fbData of adsInsightsArray) {
            const output = {};

            // Basic Info
            output.ad_id = fbData.ad_id; // <<< --- ADDING AD_ID HERE ---
            output.date_start = fbData.date_start;
            output.date_stop = fbData.date_stop;
            output.campaign_name = fbData.campaign_name;
            output.adset_name = fbData.adset_name;
            output.ad_name = fbData.ad_name;
            output.objective = fbData.objective;
            output.spend = parseFloat(fbData.spend || 0);
            output.impressions = parseInt(fbData.impressions || 0);
            output.clicks = parseInt(fbData.clicks || 0);

            // Initialize specific e-commerce metrics
            output.add_to_carts_count = 0;
            output.checkouts_initiated_count = 0;
            output.purchases_count = 0;
            output.purchase_value_total = 0.0;

            // --- Define your PREFERRED canonical action_types in order of preference ---
            const PREFERRED_ACTION_ORDER = {
                atc: ['omni_add_to_cart', 'offsite_conversion.fb_pixel_add_to_cart', 'add_to_cart', 'onsite_web_app_add_to_cart', 'onsite_web_add_to_cart'],
                checkout: ['omni_initiated_checkout', 'offsite_conversion.fb_pixel_initiate_checkout', 'initiate_checkout', 'onsite_web_initiate_checkout'],
                purchase_count: ['omni_purchase', 'offsite_conversion.fb_pixel_purchase', 'purchase', 'web_in_store_purchase', 'onsite_web_purchase', 'onsite_web_app_purchase'],
                purchase_value: ['omni_purchase', 'offsite_conversion.fb_pixel_purchase', 'purchase', 'web_in_store_purchase', 'onsite_web_purchase', 'onsite_web_app_purchase']
            };

            // --- Helper function to get COUNT for a specific metric from 'actions' ---
            function getActionCount(actionsData, preferredTypes) {
                if (!actionsData) return 0;
                let actionsArray = [];
                if (Array.isArray(actionsData)) {
                    actionsArray = actionsData;
                } else if (typeof actionsData === 'object' && actionsData !== null && typeof actionsData.action_type !== 'undefined') {
                    actionsArray = [actionsData]; // Handle if 'actions' is a single object
                } else {
                    return 0; // Invalid actionsData structure or empty
                }

                for (const type of preferredTypes) {
                    const action = actionsArray.find(a => a.action_type === type);
                    if (action) {
                        return parseInt(action.value || 0);
                    }
                }
                return 0;
            }

            // --- Helper function to get MONETARY VALUE for purchases from 'action_values' ---
            function getPurchaseMonetaryValue(actionValuesData, preferredTypes) {
                if (!actionValuesData || !Array.isArray(actionValuesData)) return 0.0;
                for (const type of preferredTypes) {
                    const action = actionValuesData.find(a => a.action_type === type);
                    if (action) {
                        if (type === 'web_app_in_store_purchase' && parseFloat(action.value || 0) < 1.00) {
                            const hasOtherMoreValuablePurchaseType = preferredTypes.some(pt =>
                                pt !== 'web_app_in_store_purchase' &&
                                actionValuesData.find(av => av.action_type === pt && parseFloat(av.value || 0) >= 1.00)
                            );
                            if (hasOtherMoreValuablePurchaseType) {
                                continue;
                            }
                        }
                        return parseFloat(action.value || 0);
                    }
                }
                return 0.0;
            }

            // Populate the e-commerce metrics
            output.add_to_carts_count = getActionCount(fbData.actions, PREFERRED_ACTION_ORDER.atc);
            output.checkouts_initiated_count = getActionCount(fbData.actions, PREFERRED_ACTION_ORDER.checkout);
            output.purchases_count = getActionCount(fbData.actions, PREFERRED_ACTION_ORDER.purchase_count);
            output.purchase_value_total = getPurchaseMonetaryValue(fbData.action_values, PREFERRED_ACTION_ORDER.purchase_value);

            finalOutputItems.push({ json: output });
        }
    } else {
        console.error("Input data structure is not as expected from HTTP Request. 'data' array not found or not an array.", facebookResponse);
        finalOutputItems.push({ json: { error: "Facebook response parsing error", details: "Input 'data' array not found." } });
    }
}

return finalOutputItems;
// --- END OF CODE NODE 1 SCRIPT (Process Raw Data) ---

// --- START OF CODE NODE 3 SCRIPT ---
// Input: Output from Code Node 1 (array of processed ad records)
// Mode: Run Once for All Items

const allProcessedAdRecords = $input.all(); // Array of n8n items from Code Node 1

// Initialize overall totals
const overallTotals = {
    total_spend: 0,
    total_impressions: 0,
    total_clicks: 0,
    total_add_to_carts: 0,
    total_checkouts_initiated: 0,
    total_purchases: 0,
    total_purchase_value: 0,
    // We can't really sum 'objective' or 'ad_name' in a meaningful way for an overall summary,
    // so we'll omit them or set them to a generic value.
    // We also won't sum date_start/date_stop, but we can determine the overall period.
};

let minDateStart = null;
let maxDateStop = null;

// Step 1: Sum up fundamental metrics from all processed ad records
for (const n8nItem of allProcessedAdRecords) {
    const adRecord = n8nItem.json; // Each adRecord is an output from Code Node 1

    overallTotals.total_spend += parseFloat(adRecord.spend || 0);
    overallTotals.total_impressions += parseInt(adRecord.impressions || 0);
    overallTotals.total_clicks += parseInt(adRecord.clicks || 0);
    overallTotals.total_add_to_carts += parseInt(adRecord.add_to_carts_count || 0);
    overallTotals.total_checkouts_initiated += parseInt(adRecord.checkouts_initiated_count || 0);
    overallTotals.total_purchases += parseInt(adRecord.purchases_count || 0);
    overallTotals.total_purchase_value += parseFloat(adRecord.purchase_value_total || 0);

    // Determine overall date range
    if (adRecord.date_start) {
        const currentStartDate = new Date(adRecord.date_start);
        if (!minDateStart || currentStartDate < minDateStart) {
            minDateStart = currentStartDate;
        }
    }
    if (adRecord.date_stop) {
        const currentStopDate = new Date(adRecord.date_stop);
        if (!maxDateStop || currentStopDate > maxDateStop) {
            maxDateStop = currentStopDate;
        }
    }
}

// Add overall date range to the output
if (minDateStart) {
    overallTotals.overall_date_start = minDateStart.toISOString().split('T')[0]; // YYYY-MM-DD
}
if (maxDateStop) {
    overallTotals.overall_date_stop = maxDateStop.toISOString().split('T')[0]; // YYYY-MM-DD
}


// Step 2: Calculate overall derived KPIs based on the grand totals
const overallKPIs = { ...overallTotals }; // Start with the summed totals

overallKPIs.ctr = overallTotals.total_impressions > 0 ? (overallTotals.total_clicks / overallTotals.total_impressions) : 0;
overallKPIs.cpc = overallTotals.total_clicks > 0 ? (overallTotals.total_spend / overallTotals.total_clicks) : 0;
overallKPIs.cpm = overallTotals.total_impressions > 0 ? (overallTotals.total_spend / overallTotals.total_impressions * 1000) : 0;

overallKPIs.cost_per_add_to_cart = overallTotals.total_add_to_carts > 0 ? (overallTotals.total_spend / overallTotals.total_add_to_carts) : 0;
overallKPIs.cost_per_checkout = overallTotals.total_checkouts_initiated > 0 ? (overallTotals.total_spend / overallTotals.total_checkouts_initiated) : 0;
overallKPIs.cost_per_purchase = overallTotals.total_purchases > 0 ? (overallTotals.total_spend / overallTotals.total_purchases) : 0;

overallKPIs.roas = overallTotals.total_spend > 0 ? (overallTotals.total_purchase_value / overallTotals.total_spend) : 0;

let conversionRateDecimal = overallTotals.total_clicks > 0 ? (overallTotals.total_purchases / overallTotals.total_clicks) : 0;
overallKPIs.average_order_value = overallTotals.total_purchases > 0 ? (overallTotals.total_purchase_value / overallTotals.total_purchases) : 0;

// --- Format numbers for the overall summary ---
overallKPIs.total_spend = parseFloat(overallKPIs.total_spend.toFixed(2));
overallKPIs.total_purchase_value = parseFloat(overallKPIs.total_purchase_value.toFixed(2));

overallKPIs.ctr = parseFloat(overallKPIs.ctr.toFixed(4));
overallKPIs.cpc = parseFloat(overallKPIs.cpc.toFixed(2));
overallKPIs.cpm = parseFloat(overallKPIs.cpm.toFixed(2));

overallKPIs.cost_per_add_to_cart = parseFloat(overallKPIs.cost_per_add_to_cart.toFixed(2));
overallKPIs.cost_per_checkout = parseFloat(overallKPIs.cost_per_checkout.toFixed(2));
overallKPIs.cost_per_purchase = parseFloat(overallKPIs.cost_per_purchase.toFixed(2));

overallKPIs.roas = parseFloat(overallKPIs.roas.toFixed(2));

overallKPIs.conversion_rate = (conversionRateDecimal * 100).toFixed(2) + '%'; // As percentage string
overallKPIs.average_order_value = parseFloat(overallKPIs.average_order_value.toFixed(2));

// This node will output a single item containing the overall summary
return [{ json: overallKPIs }];
// --- END OF CODE NODE 3 SCRIPT ---

// --- START OF CODE NODE 4 SCRIPT (Stringify Output) ---
// Mode: Run Once for All Items
// Input: The array of aggregated ad creative performance objects

const allAggregatedItems = $input.all(); // This is an array of n8n items

// Extract the .json part from each n8n item to get the actual data objects
const dataToConvert = allAggregatedItems.map(item => item.json);

// Stringify the array of data objects
// The 'null, 2' arguments pretty-print the JSON string with an indent of 2 spaces,
// which can be helpful for readability if you're inspecting it, but not strictly necessary for an LLM.
// If the LLM prefers a more compact JSON, you can omit 'null, 2'.
const jsonString = JSON.stringify(dataToConvert, null, 2);

// Output a single item containing this string
return [{ json: { benchmarkData: jsonString } }];
// --- END OF CODE NODE 4 SCRIPT ---

// --- START OF CODE NODE 2 SCRIPT (Aggregate by Ad Creative & KPIs) ---
// Mode: Run Once for All Items
// Input: Output from Code Node 1 (which now includes ad_id)

const allInputItems = $input.all();
const aggregatedByAdName = {};

// Step 1: Aggregate metrics by ad_name
for (const n8nItem of allInputItems) {
    const itemData = n8nItem.json; // Data from Code Node 1, includes ad_id
    const key = itemData.ad_name;

    if (!aggregatedByAdName[key]) {
        aggregatedByAdName[key] = {
            ad_name: itemData.ad_name,
            ad_id: itemData.ad_id, // <<< STORE THE AD_ID OF THE FIRST ENCOUNTERED ITEM
            objective: itemData.objective,
            // Initialize all sums
            total_spend: 0,
            total_impressions: 0,
            total_clicks: 0,
            total_add_to_carts: 0,
            total_checkouts_initiated: 0,
            total_purchases: 0,
            total_purchase_value: 0,
        };
    }

    // If the first itemData for an ad_name somehow missed an ad_id (unlikely if Code Node 1 is correct),
    // but a subsequent one has it, this ensures it gets populated.
    if (itemData.ad_id && !aggregatedByAdName[key].ad_id) {
        aggregatedByAdName[key].ad_id = itemData.ad_id;
    }
    // Same for objective, if needed
    if (itemData.objective && !aggregatedByAdName[key].objective) {
         aggregatedByAdName[key].objective = itemData.objective;
    }

    // Sum up the metrics
    aggregatedByAdName[key].total_spend += parseFloat(itemData.spend || 0);
    aggregatedByAdName[key].total_impressions += parseInt(itemData.impressions || 0);
    aggregatedByAdName[key].total_clicks += parseInt(itemData.clicks || 0);
    aggregatedByAdName[key].total_add_to_carts += parseInt(itemData.add_to_carts_count || 0);
    aggregatedByAdName[key].total_checkouts_initiated += parseInt(itemData.checkouts_initiated_count || 0);
    aggregatedByAdName[key].total_purchases += parseInt(itemData.purchases_count || 0);
    aggregatedByAdName[key].total_purchase_value += parseFloat(itemData.purchase_value_total || 0);
}

// Step 2: Calculate derived metrics and prepare final output
const finalOutputArray = [];
for (const key in aggregatedByAdName) {
    const adCreativeData = aggregatedByAdName[key];
    const outputItem = { ...adCreativeData }; // Includes ad_name, ad_id, objective, and all total_... fields

    // Calculate derived metrics, handling division by zero
    outputItem.ctr = adCreativeData.total_impressions > 0 ? (adCreativeData.total_clicks / adCreativeData.total_impressions) : 0;
    outputItem.cpc = adCreativeData.total_clicks > 0 ? (adCreativeData.total_spend / adCreativeData.total_clicks) : 0;
    outputItem.cpm = adCreativeData.total_impressions > 0 ? (adCreativeData.total_spend / adCreativeData.total_impressions * 1000) : 0;

    outputItem.cost_per_add_to_cart = adCreativeData.total_add_to_carts > 0 ? (adCreativeData.total_spend / adCreativeData.total_add_to_carts) : 0;
    outputItem.cost_per_checkout = adCreativeData.total_checkouts_initiated > 0 ? (adCreativeData.total_spend / adCreativeData.total_checkouts_initiated) : 0;
    outputItem.cost_per_purchase = adCreativeData.total_purchases > 0 ? (adCreativeData.total_spend / adCreativeData.total_purchases) : 0;

    outputItem.roas = adCreativeData.total_spend > 0 ? (adCreativeData.total_purchase_value / adCreativeData.total_spend) : 0;

    let conversionRateDecimal = adCreativeData.total_clicks > 0 ? (adCreativeData.total_purchases / adCreativeData.total_clicks) : 0;
    outputItem.average_order_value = adCreativeData.total_purchases > 0 ? (adCreativeData.total_purchase_value / adCreativeData.total_purchases) : 0;

    // Format numbers
    outputItem.total_spend = parseFloat(outputItem.total_spend.toFixed(2));
    outputItem.total_purchase_value = parseFloat(outputItem.total_purchase_value.toFixed(2));

    outputItem.ctr = parseFloat(outputItem.ctr.toFixed(4));
    outputItem.cpc = parseFloat(outputItem.cpc.toFixed(2));
    outputItem.cpm = parseFloat(outputItem.cpm.toFixed(2));

    outputItem.cost_per_add_to_cart = parseFloat(outputItem.cost_per_add_to_cart.toFixed(2));
    outputItem.cost_per_checkout = parseFloat(outputItem.cost_per_checkout.toFixed(2));
    outputItem.cost_per_purchase = parseFloat(outputItem.cost_per_purchase.toFixed(2));

    outputItem.roas = parseFloat(outputItem.roas.toFixed(2));

    outputItem.conversion_rate = (conversionRateDecimal * 100).toFixed(2) + '%';
    outputItem.average_order_value = parseFloat(outputItem.average_order_value.toFixed(2));

    finalOutputArray.push({ json: outputItem });
}

return finalOutputArray;
// --- END OF CODE NODE 2 SCRIPT ---
