const input = items[0].json;

const stdout = input.stdout || '';

const entries = stdout.split(/\n\n\[CVE-/).filter(e => e.trim() !== '');

const formatted = entries.map((e, i) => (i === 0 ? e : '[CVE-' + e));

return formatted.map(entry => ({ json: { CVE: entry.trim() } }));

const raw = items[0].json.CVE || items[0].json.stdout || '';
if (!raw) return [{ json: { error: 'no-input-found' } }];

const lines = raw.split(/\r?\n/);
const joinTrim = arr => arr.map(s => s.trim()).filter(Boolean).join(' ');
const extractBracketCve = s => {
  const m = s.match(/\[CVE-[0-9\-]+\]/);
  return m ? m[0].replace(/[\[\]]/g,'') : null;
};

let cveId = extractBracketCve(raw);
let severity = null;
let title = null;
let metadata = {};
let sections = { Summary: [], Risk: [], Remediation: [], POCs: [], References: [], 'Affected Products': [] };
let currentSection = null;

const firstLine = lines.find(l => l.trim().length > 0) || '';
const mFirst = firstLine.match(/^\[CVE-[^\]]+\]\s*([A-Za-z]+)\s*-\s*(.+)$/);
if (mFirst) { severity = mFirst[1].trim(); title = mFirst[2].trim(); }

for (const rawLine of lines) {
  const line = rawLine.trim();
  if (!line) continue;

  if (/^‚Ü≥/.test(line) || line.includes('‚Ü≥')) {
    const clean = line.replace(/^‚Ü≥\s*/,'').trim();
    const kv = clean.match(/^([^:]+):\s*(.+)$/);
    if (kv) metadata[kv[1].trim()] = kv[2].trim();
    else metadata.misc = (metadata.misc ? metadata.misc + ' ' : '') + clean;
    continue;
  }

  if (/^Summary\b/i.test(line)) { currentSection = 'Summary'; continue; }
  if (/^Risk\b/i.test(line)) { currentSection = 'Risk'; continue; }
  if (/^Remediation\b/i.test(line)) { currentSection = 'Remediation'; continue; }
  if (/^POC|^POCs\b/i.test(line)) { currentSection = 'POCs'; continue; }
  if (/^References\b/i.test(line)) { currentSection = 'References'; continue; }
  if (/^Affected Products\b/i.test(line)) { currentSection = 'Affected Products'; continue; }

  if (currentSection) {
    const clean = line.replace(/^‚Üí\s*/,'').trim();
    sections[currentSection].push(clean);
    continue;
  }

  const m = line.match(/^\[CVE-[^\]]+\]\s*([A-Za-z]+)\s*-\s*(.+)$/);
  if (m && (!severity || !title)) { severity = m[1].trim(); title = m[2].trim(); }
}

const results = [];

if (cveId) results.push({ json: { CVE: cveId } });
if (severity) results.push({ json: { Severity: severity } });
if (title) results.push({ json: { Title: title } });

for (const k of Object.keys(metadata)) {
  results.push({ json: { [k]: metadata[k] } });
}

for (const sec of ['Summary','Risk','Remediation']) {
  const v = joinTrim(sections[sec]);
  if (v) results.push({ json: { [sec]: v } });
}
for (const sec of ['POCs','References','Affected Products']) {
  const arr = sections[sec].filter(Boolean);
  if (arr.length) results.push({ json: { [sec]: arr } });
}

return results;

// input: items de n8n
const urlRegex = /(https?:\/\/[^\s)]+)/g;

const results = [];

items.forEach(item => {
    for (const key in item.json) {
        const value = item.json[key];
        if (typeof value === 'string') {
            const matches = value.match(urlRegex);
            if (matches) {
                matches.forEach(url => results.push({ json: { url } }));
            }
        }
    }
});

return results;

