const d = (items[0].json.deployments || [])
  .sort((a, b) => b.created - a.created)
  .slice(0); // keep 2 newest
return d.map(x => ({ json: { uid: x.uid, createdAt: x.created } }));

// Merge Chat Input + Get row(s) data

// Get chat message from the Chat Input node
const chatNode = $node["Chat input"].json;
const userMessage = (chatNode.chatInput || "").trim();

// Get any existing HTML (if available) from Get row(s)
const getRowData = $node["Get memory for current session"].json;
const currentHtml = getRowData.html || "";

// If user says "start new", clear previous HTML
let finalHtml = currentHtml;
if (userMessage.toLowerCase().includes("start new")) {
  finalHtml = "";
}

// Pass both values forward
return [
  {
    json: {
      prompt: userMessage,
      current_html: finalHtml,
      sessionId: chatNode.sessionId,
    },
  },
];

