// --- Get the HTML content from the OpenAI node ---
const html =
  $node["Generate HTML (OpenAI)"].json?.choices?.[0]?.message?.content ??
  $json.html ??
  "";

// --- Clean out markdown fences if they exist ---
const cleanedHtml = html
  .replace(/^```(?:html)?/i, "")
  .replace(/```$/, "")
  .trim();

// --- Return payload for Vercel deployment ---
return [
  {
    json: {
      name: `ai-site-${Date.now()}`,
      files: [
        {
          file: "index.html",
          data: cleanedHtml, // raw HTML string; Vercel accepts this
        },
      ],
      projectSettings: { framework: null },
      target: "production",
    },
  },
];
