// n8n Code Node - SharePoint URL with Version Parameter
// Use this in a "Code" node in n8n

const items = $input.all();

const outputItems = items.map(item => {
  const data = item.json;

  // Use existing LinkingUrl if available
  if (data.LinkingUrl) {
    return {
      json: {
        ...data,
        fileUrl: data.LinkingUrl
      }
    };
  }

  // Construct versioned URL
  let fileUrl = '';

  if (data.ServerRelativeUrl && data.ContentTag) {
    // Base URL with server relative path
    fileUrl = 'https://nosta.sharepoint.com' + data.ServerRelativeUrl;

    // Extract GUID from ContentTag and create version parameter
    const guidMatch = data.ContentTag.match(/\{([^}]+)\}/);
    if (guidMatch) {
      const versionId = guidMatch[1].toLowerCase().replace(/-/g, '');
      fileUrl += `?d=w${versionId}`;
    }
  }

  return {
    json: {
      ...data,
      fileUrl: fileUrl
    }
  };
});

return outputItems;

const DEFAULT_CHUNK_SIZE = 2000;   // chunk size
const DEFAULT_OVERLAP = 0.3;       // 30% overlap

function splitTextWithOverlap(text, chunkSize, overlap) {
  const step = Math.max(1, Math.floor(chunkSize * (1 - overlap)));
  const parts = [];
  for (let start = 0; start < text.length; start += step) {
    parts.push(text.slice(start, start + chunkSize));
  }
  return parts;
}

function extractUrl(text) {
  if (!text) return "";
  const match = text.match(/https?:\/\/[^\s)'"<>]+/);
  return match ? match[0] : "";
}

const incoming = $input.item.json;

// 1. Combine fields for embedding
const combinedText = [
  incoming.summary || "",
  incoming["cleaned markdown"] || "",
  incoming["process markdown"] || ""
].join("\n\n");

// 2. Pick only ONE URL
let url = incoming.url || "";
if (!url) {
  url = extractUrl(incoming["cleaned markdown"]) || extractUrl(incoming["raw markdwon"]);
}

// 3. Chunk
const parts = splitTextWithOverlap(combinedText, DEFAULT_CHUNK_SIZE, DEFAULT_OVERLAP);

// 4. Build output with metadata
let charPos = 0;
const response = parts.map((part, idx) => {
  const from = charPos;
  const to = charPos + part.length;
  const out = {
    content: part,
    metadata: {
      title: incoming.title || "",
      url,                 // ‚úÖ only one URL
      page: 1,
      chunk_index: idx,
      loc: { Characters: { from, to } }
    }
  };
  charPos = to;
  return { json: out };
});

return response;
