// Step 1: Get the raw text from OCR output
const rawText = $json.ParsedResults[0].ParsedText || '';

// Step 2: Clean and split into lines
const cleaned = rawText.replace(/\r/g, '').replace(/\\n/g, '\n');
const lines = cleaned.split('\n').map(l => l.trim()).filter(Boolean);

// Step 3: Helper function to support both ":" and "." after field names
function extractField(label) {
  const index = lines.findIndex(line =>
    line.toLowerCase().startsWith(label.toLowerCase() + ':') ||
    line.toLowerCase().startsWith(label.toLowerCase() + '.')
  );

  if (index !== -1) {
    return lines[index].split(/[:.]/).slice(1).join(':').trim();
  }
  return null;
}

// Step 4: Extract Amount
let amountLine = lines.find(line => line.toLowerCase().includes('total amount'));
let amount = amountLine ? amountLine.split(/[:.]/).pop().trim() : null;

// Step 5: Extract Payment Method (next 1‚Äì3 lines)
let paymentMethod = '';
const paymentIndex = lines.findIndex(l => l.toLowerCase().includes('payment method'));
if (paymentIndex !== -1) {
  for (let i = paymentIndex + 1; i <= paymentIndex + 3 && i < lines.length; i++) {
    paymentMethod += lines[i] + ' ';
  }
  paymentMethod = paymentMethod.trim();
}

// Step 6: Extract Notes (everything after Notes label)
let notes = '';
const notesIndex = lines.findIndex(l => l.toLowerCase().startsWith('notes'));
if (notesIndex !== -1) {
  notes = lines.slice(notesIndex + 1).join(' ').trim();
}

// Step 7: Return the structured data
return [
  {
    json: {
      InvoiceDate: extractField('Date'),
      DueDate: extractField('Due Date'),
      Amount: amount,
      PaymentMethod: paymentMethod,
      Notes: notes
    }
  }
];
