// Enhanced n8n code node for base64 to PNG conversion
const inputData = $input.all();
const outputData = [];

// Helper function to safely get nested property
function getNestedProperty(obj, path) {
  return path.split('.').reduce((current, key) => {
    return current && current[key] !== undefined ? current[key] : null;
  }, obj);
}

// Helper function to validate base64
function isValidBase64(str) {
  if (!str || typeof str !== 'string') return false;
  try {
    // Basic base64 pattern check
    const base64Pattern = /^[A-Za-z0-9+/]*={0,2}$/;
    return base64Pattern.test(str) && str.length % 4 === 0;
  } catch (err) {
    return false;
  }
}

// Helper function to detect image format from base64
function detectImageFormat(base64String) {
  try {
    const header = base64String.substring(0, 20);
    const decoded = Buffer.from(header, 'base64');

    // PNG signature: 89 50 4E 47
    if (decoded[0] === 0x89 && decoded[1] === 0x50 && decoded[2] === 0x4E && decoded[3] === 0x47) {
      return 'png';
    }
    // JPEG signature: FF D8 FF
    if (decoded[0] === 0xFF && decoded[1] === 0xD8 && decoded[2] === 0xFF) {
      return 'jpeg';
    }
    // GIF signature: 47 49 46
    if (decoded[0] === 0x47 && decoded[1] === 0x49 && decoded[2] === 0x46) {
      return 'gif';
    }
    // WebP signature: starts with "RIFF" and contains "WEBP"
    const str = decoded.toString('ascii', 0, 12);
    if (str.startsWith('RIFF') && str.includes('WEBP')) {
      return 'webp';
    }
    return 'unknown';
  } catch (error) {
    return 'unknown';
  }
}

for (const [index, item] of inputData.entries()) {
  try {
    let base64String = null;
    let sourcePath = null;

    // Define possible paths where base64 data might be located
    const possiblePaths = [
      'candidates.0.content.parts.1.inlineData.data',
      'candidates.0.content.parts.0.inlineData.data',
      // Add other possible variations
      'candidates.0.content.parts.1.inline_data.data',
      'candidates.0.content.parts.0.inline_data.data',
      // Check if there are multiple parts
      'candidates.0.content.parts.2.inlineData.data',
      'candidates.0.content.parts.3.inlineData.data',
      // Fallback to common base64 field names
      'base64',
      'data',
      'image',
      'content'
    ];

    // Try to find base64 data in any of the possible paths
    for (const path of possiblePaths) {
      const value = getNestedProperty(item.json, path);
      if (value && typeof value === 'string' && value.length > 100) { // Minimum length check for image data
        // Handle data URL format if present
        let cleanValue = value;
        if (value.includes('data:image/') && value.includes('base64,')) {
          cleanValue = value.split('base64,')[1];
        }

        // Validate if it's proper base64
        if (isValidBase64(cleanValue)) {
          base64String = cleanValue;
          sourcePath = path;
          break;
        }
      }
    }

    // Also check if there are multiple parts and scan through them
    if (!base64String && item.json.candidates && item.json.candidates[0] && item.json.candidates[0].content && item.json.candidates[0].content.parts) {
      const parts = item.json.candidates[0].content.parts;
      for (let i = 0; i < parts.length; i++) {
        if (parts[i] && parts[i].inlineData && parts[i].inlineData.data) {
          const value = parts[i].inlineData.data;
          if (isValidBase64(value)) {
            base64String = value;
            sourcePath = `candidates.0.content.parts.${i}.inlineData.data`;
            break;
          }
        }
        // Also check for inline_data (underscore variant)
        if (parts[i] && parts[i].inline_data && parts[i].inline_data.data) {
          const value = parts[i].inline_data.data;
          if (isValidBase64(value)) {
            base64String = value;
            sourcePath = `candidates.0.content.parts.${i}.inline_data.data`;
            break;
          }
        }
      }
    }

    if (!base64String) {
      throw new Error('No valid base64 image data found in any of the expected paths');
    }

    // Convert to buffer
    const imageBuffer = Buffer.from(base64String, 'base64');

    // Detect original format
    const originalFormat = detectImageFormat(base64String);

    // Generate filename
    const timestamp = new Date().getTime();
    const filename = `converted_image_${timestamp}_${index}.png`;

    // Create output item
    const outputItem = {
      json: {
        success: true,
        filename: filename,
        mimeType: 'image/png',
        fileSize: imageBuffer.length,
        originalFormat: originalFormat,
        sourcePath: sourcePath,
        convertedAt: new Date().toISOString(),
        itemIndex: index
      },
      binary: {
        data: {
          data: base64String, // Use the clean base64 string
          mimeType: 'image/png',
          fileName: filename,
          fileExtension: 'png'
        }
      }
    };

    outputData.push(outputItem);

  } catch (error) {
    // Create error output with debugging information
    const errorItem = {
      json: {
        success: false,
        error: error.message,
        itemIndex: index,
        availablePaths: Object.keys(item.json).length > 0 ? 
          JSON.stringify(item.json, null, 2).substring(0, 500) + '...' : 'No data',
        timestamp: new Date().toISOString()
      }
    };

    outputData.push(errorItem);
  }
}

return outputData;
