const travelKeywords = ['trip', 'travel', 'vacation', 'flight', 'holiday'];

return items.filter(item => {
  const title = (item.json.summary || '').toLowerCase();
  const description = (item.json.description || '').toLowerCase();

  return travelKeywords.some(keyword => title.includes(keyword) || description.includes(keyword));
});

return items.map(item => {
  const startDate = item.json.start.dateTime || item.json.start.date;
  const endDate = item.json.end.dateTime || item.json.end.date;

  // Initialize location variable
  let location = null;

  // Extract destination from summary first (e.g. "Flight to Istanbul")
  const summary = (item.json.summary || '').toLowerCase();
  const matchSummary = summary.match(/to ([a-zA-Z\s,]+)/);
  if (matchSummary && matchSummary[1]) {
    location = matchSummary[1].trim();
  }

  // If no destination yet, try description (e.g. "Flight from Mauritius (port Louis) to Istanbul")
  if (!location) {
    const description = (item.json.description || '').toLowerCase();
    // Match pattern after "to "
    const matchDescription = description.match(/to ([a-zA-Z\s,]+)/);
    if (matchDescription && matchDescription[1]) {
      location = matchDescription[1].trim();
    }
  }

  // Only use the location field if above patterns fail or if location is not a "from" location
  if (!location && item.json.location) {
    const locLower = item.json.location.toLowerCase();
    if (!locLower.startsWith('from ')) {  // Avoid departure locations starting with "from"
      location = item.json.location;
    }
  }

  // Final fallback if no location found
  if (!location) {
    location = null; // or 'YOUR_DEFAULT_TRIP_LOCATION'
  }

  return {
    json: {
      startDate,
      endDate,
      location
    }
  };
});

