return items.map(item => {
  let forecastSummary = `Weather forecast for your trip to ${item.json.address || 'destination'} from ${item.json.days[0].datetime} to ${item.json.days[item.json.days.length - 1].datetime}:\n`;

  item.json.days.forEach(day => {
    const precipprob = day.precipprob !== undefined ? `, Precip Prob: ${day.precipprob}%` : '';
    const preciptype = day.preciptype ? `, Precip Type: ${day.preciptype.join(", ")}` : '';
    const conditions = day.conditions ? `, Conditions: ${day.conditions}` : '';
    const description = day.description ? `,  ${day.description}` : '';
    forecastSummary += `${day.datetime}: Max ${day.tempmax}¬∞C, Min ${day.tempmin}¬∞C${precipprob}${preciptype}${conditions}${description}\n`;
  });

  if (item.json.alerts && item.json.alerts.length > 0) {
    forecastSummary += `\nALERTS:\n`;
    item.json.alerts.forEach(alert => {
      forecastSummary += `${alert.description}\n`;
    });
  }

  return {
    json: { forecastSummary }
  };
});
