// Get the AI's response from the current input
const aiOutput = $input.first().json.output;

// Get the original messages from the prep node
const allMessages = $('prep messages for AI').first().json.originalMessages;

// Get configuration from prep node
const config = $('prep messages for AI').first().json.config;

// Debug logs
console.log('AI Output:', aiOutput);
console.log('All Messages:', allMessages);

// Parse the AI response
let badIndices = [];
try {
  if (Array.isArray(aiOutput)) {
    badIndices = aiOutput;
  } else if (typeof aiOutput === 'string') {
    badIndices = JSON.parse(aiOutput);
  }
} catch (error) {
  console.log('Parse error:', error);
  return [];
}

console.log('Bad Indices (before dedup):', badIndices);

// Remove duplicates from badIndices
badIndices = [...new Set(badIndices)];

console.log('Bad Indices (after dedup):', badIndices);

// Validate indices exist in the array
badIndices = badIndices.filter(index => 
  index >= 0 && index < allMessages.length && allMessages[index]
);

console.log('Bad Indices (validated):', badIndices);

// Get only the unique bad messages
const messagesToDelete = badIndices.map(index => ({
  json: {
    messageId: allMessages[index].id,
    content: allMessages[index].content,
    author: allMessages[index].author,
    config: config  // Pass config forward
  }
}));

// Additional safety: Remove duplicate message IDs
const uniqueMessages = [];
const seenIds = new Set();

for (const msg of messagesToDelete) {
  if (!seenIds.has(msg.json.messageId)) {
    seenIds.add(msg.json.messageId);
    uniqueMessages.push(msg);
  }
}

console.log('Final unique messages to delete:', uniqueMessages.length);

return uniqueMessages.length > 0 ? uniqueMessages : [];

// ===== CONFIGURATION - EDIT THESE VALUES =====
const CONFIG = {
  discordServerId: '1373767704351346689',        // Your Discord Server ID
  moderatedChannelId: '1429758114948841592',     // Channel to monitor
  adminChannelId: '1427336562240126976'          // Admin notification channel
};
// YOUR_AWS_SECRET_KEY_HERE====

// Get training examples from Google Sheets (if available)
let badExamples = '';
let goodExamples = '';

try {
  const examples = $('Get sheet knowledgebase').all().map(item => item.json);

  // Format bad examples (messages to DELETE)
  const badList = examples
    .filter(e => e.should_delete === 'YES')
    .map(e => `"${e.message_content}" (Reason: ${e.reason})`)
    .join('\n- ');

  if (badList) {
    badExamples = `\n\nMessages to ALWAYS DELETE:\n- ${badList}`;
  }

  // Format good examples (messages to KEEP)
  const goodList = examples
    .filter(e => e.should_delete === 'NO')
    .map(e => `"${e.message_content}" (Reason: ${e.reason})`)
    .join('\n- ');

  if (goodList) {
    goodExamples = `\n\nMessages to NEVER DELETE:\n- ${goodList}`;
  }
} catch (error) {
  console.log('No training examples found or Google Sheets not connected:', error);
}

// Get all messages from Discord
const messages = $input.all().map(item => item.json);

// Create a formatted list with IDs for the AI
const messageList = messages.map((msg, index) => 
  `[${index}] ID: ${msg.id}\nAuthor: ${msg.author.username}\nContent: ${msg.content}`
).join('\n\n---\n\n');

return [{
  json: {
    messageList,
    originalMessages: messages,
    trainingExamples: badExamples + goodExamples,
    // Pass config along with the data
    config: CONFIG
  }
}];
