const webhookData = $input.first().json.body;
const photoAnalysis = JSON.parse($input.all()[1].json.choices[0].message.content);
const signatureAnalysis = JSON.parse($input.all()[2].json.choices[0].message.content);

const photoVerified = photoAnalysis.verified && photoAnalysis.confidence > 0.7;
const signatureValid = signatureAnalysis.valid && signatureAnalysis.confidence > 0.7;

const overallStatus = photoVerified && signatureValid ? 'verified' : 'disputed';

return {
  json: {
    delivery_id: webhookData.delivery_id,
    driver_id: webhookData.driver_id || 'UNKNOWN',
    recipient_name: webhookData.recipient_name || '',
    delivery_status: overallStatus,
    verification: {
      photo: {
        verified: photoVerified,
        confidence: photoAnalysis.confidence,
        issues: photoAnalysis.issues || [],
        description: photoAnalysis.description
      },
      signature: {
        valid: signatureValid,
        confidence: signatureAnalysis.confidence,
        concerns: signatureAnalysis.concerns || []
      }
    },
    gps_coordinates: webhookData.gps_coordinates || null,
    timestamp: new Date().toISOString(),
    images: {
      signature: webhookData.signature_image,
      delivery: webhookData.delivery_photo
    }
  }
};

