const script = $input.first().json.choices[0].message.content;

// Parse script into scenes
const scenes = [];
const lines = script.split('\n').filter(line => line.trim());

let currentScene = {
  number: 1,
  voiceover: '',
  visuals: '',
  duration: 5
};

for (const line of lines) {
  if (line.match(/^Scene \d+:/i) || line.match(/^\[Scene \d+\]/i)) {
    if (currentScene.voiceover) {
      scenes.push({...currentScene});
      currentScene = {
        number: scenes.length + 1,
        voiceover: '',
        visuals: '',
        duration: 5
      };
    }
  } else if (line.match(/^Voiceover:/i) || line.match(/^VO:/i)) {
    currentScene.voiceover += line.replace(/^(Voiceover:|VO:)/i, '').trim() + ' ';
  } else if (line.match(/^Visual:/i) || line.match(/^Visuals:/i)) {
    currentScene.visuals = line.replace(/^(Visual:|Visuals:)/i, '').trim();
  } else if (currentScene.voiceover === '') {
    currentScene.voiceover += line.trim() + ' ';
  }
}

if (currentScene.voiceover) {
  scenes.push(currentScene);
}

// If no structured scenes found, create single scene
if (scenes.length === 0) {
  scenes.push({
    number: 1,
    voiceover: script,
    visuals: $input.first().json.prompt || 'relevant stock footage',
    duration: 10
  });
}

return scenes.map(scene => ({
  json: {
    sceneNumber: scene.number,
    voiceoverText: scene.voiceover.trim(),
    visualDescription: scene.visuals || 'stock footage',
    estimatedDuration: scene.duration,
    fullScript: script
  }
}));

const items = $input.all();
const result = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i].json;

  // Get the first available video file
  let videoUrl = '';
  if (item.videos && item.videos.length > 0) {
    const video = item.videos[0];
    if (video.video_files && video.video_files.length > 0) {
      // Find HD quality video
      const hdVideo = video.video_files.find(vf => vf.quality === 'hd') || video.video_files[0];
      videoUrl = hdVideo.link;
    }
  }

  result.push({
    json: {
      sceneNumber: item.sceneNumber,
      videoUrl: videoUrl,
      videoId: item.videos?.[0]?.id || i,
      duration: item.videos?.[0]?.duration || 5
    }
  });
}

return result;

// Generate SRT subtitle format
const items = $input.all();
let srtContent = '';
let startTime = 0;

for (let i = 0; i < items.length; i++) {
  const scene = items[i].json;
  const text = scene.voiceoverText || '';

  // Estimate duration based on text length (average speaking rate: 150 words/min)
  const words = text.split(' ').length;
  const duration = Math.max(3, Math.ceil((words / 150) * 60));

  const endTime = startTime + duration;

  // Format timestamps for SRT
  const formatTime = (seconds) => {
    const hrs = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);
    const ms = Math.floor((seconds % 1) * 1000);
    return `${String(hrs).padStart(2, '0')}:${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')},${String(ms).padStart(3, '0')}`;
  };

  srtContent += `${i + 1}\n`;
  srtContent += `${formatTime(startTime)} --> ${formatTime(endTime)}\n`;
  srtContent += `${text}\n\n`;

  startTime = endTime;
}

return [{
  json: {
    srtContent: srtContent,
    totalDuration: startTime
  },
  binary: {
    subtitles: {
      data: Buffer.from(srtContent).toString('base64'),
      mimeType: 'text/plain',
      fileName: 'subtitles.srt'
    }
  }
}];
