const response = $input.first().json;
const chatId = $('Extract Photo and text').first().json?.message?.chat?.id;

if (!chatId) {
  throw new Error('Chat ID not found in workflow data');
}

// OpenAI node with JSON output returns parsed object directly in message.content
const scriptContent = response?.message?.content || response?.content || {};

return [{
  json: {
    script: JSON.stringify(scriptContent),
    frames: scriptContent.frames || [],
    hashtags: scriptContent.hashtags || [],
    chatId: chatId
  }
}];

const response = $input.first().json;
const chatId = $('Extract Photo and text').first().json?.message?.chat?.id;

if (!response?.request_id) {
  throw new Error('Request ID not found in API response');
}

if (!chatId) {
  throw new Error('Chat ID not found in workflow data');
}

return [{
  json: {
    requestId: response.request_id,
    chatId: chatId,
    pollingAttempt: 0
  }
}];

const response = $input.first().json;
const previousData = $('Extract Video Job ID').first().json;

if (!previousData) {
  throw new Error('Previous polling data not found');
}

return [{
  json: {
    status: response?.status || 'UNKNOWN',
    videoUrl: response?.video?.url || null,
    requestId: previousData.requestId,
    chatId: previousData.chatId,
    pollingAttempt: (previousData.pollingAttempt || 0) + 1
  }
}];

