// Toma todos los items del Merge
const items = $input.all();

// 1) Localiza page_id y el texto de Gemini
const pageId =
  items.find(it => it.json?.page_id)?.json.page_id ?? null;

const rawText =
  items.find(it => it.json?.content)?.json?.content?.parts?.[0]?.text ?? "";

// 2) Limpia fences ```json ... ``` y espacios
const cleaned = String(rawText)
  .replace(/```json/gi, "")
  .replace(/```/g, "")
  .trim();

// 3) Intenta parsear JSON. Si falla, deja objeto vac√≠o.
let parsed = {};
try {
  parsed = cleaned ? JSON.parse(cleaned) : {};
} catch (_) {
  parsed = {};
}

// 4) Extrae el array de trozos con tolerancia de nombres
const chunks =
  Array.isArray(parsed.chunks) ? parsed.chunks :
  Array.isArray(parsed.summary_chunks) ? parsed.summary_chunks :
  Array.isArray(parsed.slices) ? parsed.slices :
  [];

// 5) Si no hay page_id o no hay chunks, no emites nada √∫til
if (!pageId || !Array.isArray(chunks) || chunks.length === 0) {
  // Opcional: emite un item de depuraci√≥n para ver qu√© lleg√≥
  return [{
    json: {
      __parse_debug: true,
      page_id_present: !!pageId,
      got_chunks: Array.isArray(chunks) ? chunks.length : -1,
      preview: cleaned.slice(0, 280)
    }
  }];
}

// 6) Emite un item por chunk: { page_id, slice, chunk_index, slice_index }
return chunks.map((chunk, i) => ({
  json: {
    page_id: pageId,
    slice: String(chunk).trim(),
    chunk_index: i,
    slice_index: 0,
  }
}));

// === DEDUPLICADOR PERSISTENTE ===
// Guarda IDs ya vistos entre ejecuciones (funciona en "Code" con modo Run Once for All Items)

const store = $getWorkflowStaticData('global'); // ‚úÖ Correcta sintaxis en n8n actual
if (!store.seen) store.seen = {}; // mapa id -> fecha
const seen = store.seen;

// Dedupe dentro de la misma ejecuci√≥n
const batchSeen = new Set();
const out = [];

for (const it of items) {
  const id = it.json.arxiv_id || it.json.link || it.json.id;
  if (!id) continue;

  if (batchSeen.has(id)) continue;  // ya apareci√≥ en esta ejecuci√≥n
  batchSeen.add(id);

  if (seen[id]) continue;           // ya apareci√≥ en ejecuciones previas

  // marcar como nuevo
  seen[id] = new Date().toISOString();
  out.push(it);
}

// Limpiar registros viejos si excede 2000 entradas
const MAX = 2000;
const entries = Object.entries(seen)
  .sort((a, b) => new Date(b[1]) - new Date(a[1]));
if (entries.length > MAX) {
  const keep = new Set(entries.slice(0, MAX).map(([k]) => k));
  for (const k of Object.keys(seen)) {
    if (!keep.has(k)) delete seen[k];
  }
}

return out;

