return items.map(item => ({
  json: {
    parent: { database_id: "TU_DATABASE_ID" },
    properties: {
      title: {
        title: [{ text: { content: item.json.title || "Sin t√≠tulo" } }]
      },
      arxiv_id: {
        rich_text: [{ text: { content: item.json.arxiv_id || "" } }]
      },
      abstract: {
        rich_text: [{ text: { content: item.json.summary || "" } }]
      },
      authors: {
        rich_text: [{ text: { content: item.json.authors || "" } }]
      },
      categories: {
        rich_text: [{ text: { content: item.json.categories || "" } }]
      },
      published: {
        date: { start: item.json.published || null }
      },
      link: {
        url: item.json.link || null
      }
    }
  }
}));

// === Configuraci√≥n ===
const HOURS = 24; // Cambia a 48 o 72 si quieres ampliar el rango
const now = new Date();
const threshold = new Date(now.getTime() - HOURS * 60 * 60 * 1000);

// === Utilidad para limpiar texto ===
const get = v => Array.isArray(v) ? String(v[0] ?? '').trim() : String(v ?? '').trim();

// === Procesar cada paper ===
const out = [];

for (const it of items) {
  const e = it.json;

  // Convertimos fechas
  const published = new Date(get(e.published || e.updated));
  if (isNaN(published) || published < threshold) continue; // filtra los m√°s antiguos

  // --- Autores ---
  const authorList = Array.isArray(e.author) ? e.author : [e.author];
  const authors = authorList
    .filter(Boolean)
    .map(a => get(a.name))
    .join(', ');

  // --- Categor√≠as ---
  const categoryList = Array.isArray(e.category) ? e.category : [e.category];
  const categories = categoryList
    .filter(Boolean)
    .map(c => c?.['@_term'] || c?.['$'] || '')
    .filter(Boolean)
    .join(', ');

  // --- A√±adir al resultado ---
  out.push({
    json: {
      arxiv_id: get(e.id),
      title: get(e.title),
      summary: get(e.summary),
      authors,
      categories,
      published: published.toISOString(),
      link: get(e.id)
    }
  });
}

return out;
