// Function to clean HTML and remove the related section
function stripHtml(html) {
  if (!html) return '';
  return html
    .replace(/<style[^>]*>.*?<\/style>/gi, '')
    .replace(/<script[^>]*>.*?<\/script>/gi, '')
    .replace(/<div class="related-articles"[^>]*>[\s\S]*?<\/div>/gi, '') // remove related articles 
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .trim();
}

// Detect what we receive from previous node
let allItems = $input.all();
let articles = [];

//  1: if multiple items
if (allItems.length > 0) {
  allItems.forEach(item => {
    // Verificar si el item contiene un array "articles"
    if (item.json && Array.isArray(item.json.articles)) {
      articles = articles.concat(item.json.articles);
    } 
    // if only one item
    else if (item.json && item.json.id && item.json.title) {
      articles.push(item.json);
    }
  });
}

// Fallback if no articles found
if (articles.length === 0 && $input.item.json) {
  if (Array.isArray($input.item.json.articles)) {
    articles = $input.item.json.articles;
  } else if ($input.item.json.id && $input.item.json.title) {
    articles = [$input.item.json];
