const uniqueIds = new Set(allArticles.map(a => a.shopify_id));

if (allArticles.length < 2) {
  throw new Error(`‚ùå Se necesitan al menos 2 art√≠culos. Solo hay ${allArticles.length}`);
}

if (uniqueIds.size < 2) {
  throw new Error(`‚ùå Todos los art√≠culos tienen el mismo ID (${Array.from(uniqueIds)[0]})`);
}

// Function to calculate similarities
function cosineSimilarity(vecA, vecB) {
  if (!vecA || !vecB || vecA.length !== vecB.length) {
    throw new Error('‚ùå Embeddings inv√°lidos o de diferente longitud');
  }

  let dotProduct = 0;
  let normA = 0;
  let normB = 0;

  for (let i = 0; i < vecA.length; i++) {
    dotProduct += vecA[i] * vecB[i];
    normA += vecA[i] * vecA[i];
    normB += vecB[i] * vecB[i];
  }

  const similarity = dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
  return Math.round(similarity * 100 * 100) / 100;
}

const relations = [];
let comparacionesRealizadas = 0;

for (let i = 0; i < allArticles.length; i++) {
  for (let j = 0; j < allArticles.length; j++) {

    // ‚ö†Ô∏è Rule to avoid comparing one article to itself
    if (allArticles[i].shopify_id === allArticles[j].shopify_id) {
      continue; // Saltar esta iteraci√≥n
    }

    comparacionesRealizadas++;

    try {
      const similarity = cosineSimilarity(
        allArticles[i].embedding,
        allArticles[j].embedding
      );

      // Only keep similarity above the setting
      if (similarity >= $('Workflow Configuration').first().json.percent_minimum_similarity) {
        relations.push({
          source_shopify_id: allArticles[i].shopify_id,
          source_blog_id: allArticles[i].blog_id,
          source_title: allArticles[i].title,
          related_shopify_id: allArticles[j].shopify_id,
          related_title: allArticles[j].title,
          related_url: allArticles[j].url,
          relation_type: "semantic_similarity",
          similarity_score: similarity
        });
      }
    } catch (error) {
      console.error(`Error comparando ${allArticles[i].shopify_id} con ${allArticles[j].shopify_id}:`, error.message);
    }
  }
}

// In case, not enough similarity
if (relations.length === 0) {
  return [{ 
    json: { 
      mensaje: "No se encontraron relaciones con similitud >= 60%",
      articulos_procesados: allArticles.length,
      articulos_unicos: uniqueIds.size,
      comparaciones_realizadas: comparacionesRealizadas,
      sugerencia: "Intenta bajar el umbral de similitud o verifica que los embeddings sean correctos",
      muestra_articulos: allArticles.slice(0, 3).map(a => ({
        shopify_id: a.shopify_id,
        title: a.title,
        longitud_embedding: a.embedding.length
      }))
    } 
  }];
}

return relations.map(rel => ({ json: rel }));

