const relations = $("Calculate similarities").all().map(item => item.json);

// Group by source_shopify_id
const groupedBySource = relations.reduce((acc, relation) => {
  const sourceId = relation.source_shopify_id;

  if (!acc[sourceId]) {
    acc[sourceId] = {
      source_shopify_id: sourceId,
      source_blog_id: relation.source_blog_id,
      source_title: relation.source_title,
      source_url: relation.source_url,
      related_articles: []
    };
  }

  // Add related article with its similarity score
  acc[sourceId].related_articles.push({
    related_shopify_id: relation.related_shopify_id,
    related_title: relation.related_title,
    related_url: relation.related_url,
    similarity_score: relation.similarity_score
  });

  return acc;
}, {});

// Convert to array and get top 3 for each source
const result = Object.values(groupedBySource).map(group => {
  // Sort by similarity_score (highest first) and take top 3
  const top3Related = group.related_articles
    .sort((a, b) => b.similarity_score - a.similarity_score)
    .slice(0, 3)
    .map(article => article.related_shopify_id);

  return {
    source_shopify_id: group.source_shopify_id,
    related_shopify_ids: top3Related
  };
});

// Return as separate items for next node
return result.map(item => ({ json: item }));

