// Get all items from stocks sheet
const items = $input.all();

// Extract cash amount from row with "Account cash" or similar
const cashRow = items.find(item => 
  item.json['Stock Name'] && 
  item.json['Stock Name'].toLowerCase().includes('cash') &&
  !item.json['Stock Symbol'] // Empty stock symbol indicates cash row
);
const availableCash = cashRow ? parseFloat(cashRow.json['Buy In Price (Full ammount invested including multiple shares']) || 0 : 0;

// Extract tickers from Stocks - UPDATED FOR FRACTIONAL SHARES AND WATCHING
const stocks = items
  .filter(item => {
      const symbol = item.json['Stock Symbol'] ? item.json['Stock Symbol'].toString().trim() : '';
      const hasSymbol = symbol.length > 0;
      const hasBuyPrice = !!item.json['Buy In Price (Full ammount invested including multiple shares'];
      const isWatching = item.json['Watching (No Buy)'] === true || item.json['Watching (No Buy)'] === 'TRUE' || item.json['Watching (No Buy)'] === '1';
      return hasSymbol && (hasBuyPrice || isWatching);
  })
  .map(item => {
    const amountSpent = parseFloat(item.json['Buy In Price (Full ammount invested including multiple shares']) || 0; // Column D
    const priceAtPurchase = parseFloat(item.json['Price at time of purchase']) || 0; // Column E
    const isFractional = item.json['Fractional?'] === true || item.json['Fractional?'] === 'TRUE' || item.json['Fractional?'] === '1'; // Column F
    const isWatching = item.json['Watching (No Buy)'] === true || item.json['Watching (No Buy)'] === 'TRUE'; // Column G
    const watchDate = item.json['Date Started Watching']; // Column H
    const watchPrice = parseFloat(item.json['Price at time of Watch Start']) || 0; // Column I

    const sharesOwned = priceAtPurchase > 0 ? (amountSpent / priceAtPurchase) : 0;

    return {
      ticker: item.json['Stock Symbol'],
      name: item.json['Stock Name'],
      buyDate: item.json['Buy In Date'],
      amountSpent: amountSpent, // Dollar amount invested
      priceAtPurchase: priceAtPurchase, // Stock price when purchased
      sharesOwned: sharesOwned, // Calculated shares (fractional or whole)
      isFractional: isFractional,
      isWatching: isWatching,
      watchDate: watchDate,
      watchPrice: watchPrice,
      type: isWatching ? 'watching' : 'stock'
    };
  });

// Get unique list of all tickers for API call
const allTickers = [...new Set(stocks.map(s => s.ticker))];

// Calculate total invested amount (exclude watching)
const totalInvested = stocks.filter(s => !s.isWatching).reduce((sum, stock) => sum + stock.amountSpent, 0);

return [{
  json: {
    stocks,
    allTickers: allTickers.join(','),
    availableCash: availableCash,
    totalInvested: totalInvested,
    totalPortfolioValue: totalInvested + availableCash
  }
}];

