// Safely stringify output and errors
const formattedOutput = typeof aiOutput === 'object' ? JSON.stringify(aiOutput, null, 2) : aiOutput;
const formattedErrors = Array.isArray(validationErrors) ? validationErrors.join('\n- ') : validationErrors;

return [
  {
