parameters.jsCode:
// Get the enriched_data array from the input
const inputData = $json.enriched_data || [];

// Clean and filter data for each prospect
const cleanedData = inputData.map(prospect => {
  const data = prospect.data;

  // Extract current professional email
  let currentEmail = null;
  if (data.emails && Array.isArray(data.emails)) {
    const currentProfessionalEmail = data.emails.find(
      email => email.type === "current_professional" && email.address
    );
    currentEmail = currentProfessionalEmail?.address || null;
  }

  // Extract most recent work experience (is_primary = true)
  let currentExperience = null;
  if (data.experience && Array.isArray(data.experience)) {
    currentExperience = data.experience.find(exp => exp.is_primary === true);

    // If no primary, get the first one (usually most recent)
    if (!currentExperience && data.experience.length > 0) {
      currentExperience = data.experience[0];
    }
  }

  // Return cleaned prospect data wrapped in { json: ... } format
  return {
    json: {
      prospect_id: prospect.prospect_id,
      full_name: data.full_name,
      email: currentEmail,
      professional_email_status: data.professional_email_status,
      mobile_phone: data.mobile_phone,
      job_title: data.job_title,
      job_department: data.job_department,
      job_seniority_level: data.job_seniority_level,
      company_name: data.company_name,
      company_website: data.company_website,
      company_linkedin: data.company_linkedin,
      linkedin: data.linkedin,
      location: {
        city: data.city,
        region: data.region_name,
        country: data.country_name
      },
      current_experience: currentExperience ? {
        company_name: currentExperience.company?.name,
        company_website: currentExperience.company?.website,
        title: currentExperience.title?.name,
        seniority: currentExperience.title?.levels?.[0],
        role: currentExperience.title?.role,
        start_date: currentExperience.start_date,
        end_date: currentExperience.end_date || null,
        summary: currentExperience.summary || null
      } : null,
      skills: data.skills || [],
      age_group: data.age_group,
      gender: data.gender
    }
  };
});

// Return as multiple items (not wrapped in data array)
return cleanedData;

