// Parse the search_results array and return individual job items
const inputData = $input.first().json;
const searchResults = inputData.search_results;

// Handle both string and array cases
let jobsArray;
if (!searchResults) {
  return [];
}

if (typeof searchResults === 'string') {
  try {
    jobsArray = JSON.parse(searchResults);
  } catch (e) {
    console.error('Error parsing search_results:', e);
    return [];
  }
} else {
  jobsArray = searchResults;
}

// Ensure jobsArray is an array
if (!Array.isArray(jobsArray)) {
  console.error('search_results is not an array:', typeof jobsArray);
  return [];
}

// Filter out any null or undefined jobs
const validJobs = jobsArray.filter(job => job && job.title);

return validJobs.map(job => ({ json: job }));
