// Check if AI extraction succeeded
if ($json.error) {
	return {
		json: {
			error_occurred: true,
			error_type: "AI_EXTRACTION_FAILED",
			error_message: $json.error.message || "AI extraction failed",
			original_email: $('Get many messages (1)').item.json,
			timestamp: new Date().toISOString()
		}
	};
}

if (!$json.output) {
	return {
		json: {
			error_occurred: true,
			error_type: "NO_OUTPUT",
			error_message: "AI did not return any output",
			original_email: $('Get many messages (1)').item.json,
			timestamp: new Date().toISOString()
		}
	};
}

try {
	let bookingData;

	if (typeof $json.output === 'string') {
		bookingData = JSON.parse($json.output);
	} else {
		bookingData = $json.output;
	}

	console.log("Parsed booking data:", JSON.stringify(bookingData, null, 2));

	const requiredFields = ['travel_agency_name', 'number_of_rooms', 'check_in_date', 'check_out_date'];
	const missingFields = [];

	for (const field of requiredFields) {
		if (!bookingData[field] || bookingData[field] === null || bookingData[field] === "null" || bookingData[field] === "") {
			missingFields.push(field);
		}
	}

	if (missingFields.length > 0) {
		console.log("Missing fields detected:", missingFields);
		return {
			json: {
				error_occurred: true,
				error_type: "MISSING_REQUIRED_FIELDS",
				error_message: `Missing required fields: ${missingFields.join(', ')}`,
				extracted_data: bookingData,
				original_email: $('Get many messages (1)').item.json,
				timestamp: new Date().toISOString()
			}
		};
	}

	const checkInDate = new Date(bookingData.check_in_date);
	const checkOutDate = new Date(bookingData.check_out_date);

	if (isNaN(checkInDate.getTime()) || isNaN(checkOutDate.getTime())) {
		console.log("Invalid date format detected");
		return {
			json: {
				error_occurred: true,
				error_type: "INVALID_DATE_FORMAT",
				error_message: "Check-in or check-out date is invalid",
				extracted_data: bookingData,
				original_email: $('Get many messages (1)').item.json,
				timestamp: new Date().toISOString()
			}
		};
	}

	if (checkOutDate <= checkInDate) {
		console.log("Date logic error detected");
		return {
			json: {
				error_occurred: true,
				error_type: "INVALID_DATE_LOGIC",
				error_message: "Check-out date must be after check-in date",
				extracted_data: bookingData,
				original_email: $('Get many messages (1)').item.json,
				timestamp: new Date().toISOString()
			}
		};
	}

	console.log("Validation successful!");
	return {
		json: {
			error_occurred: false,
			validated_data: bookingData
		}
	};

} catch (error) {
	console.log("JSON parse error:", error.message);
	return {
		json: {
			error_occurred: true,
			error_type: "JSON_PARSE_ERROR",
			error_message: error.message,
			raw_output: $json.output,
			original_email: $('Get many messages (1)').item.json,
			timestamp: new Date().toISOString()
		}
	};
}

const bookingData = $json.validated_data;

let assignedTeam = "";
let teamEmail = "";
let priority = "Normal";
let status = "New";

const numRooms = parseInt(bookingData.number_of_rooms) || 0;

// Rule 1: Assignment based on number of rooms
if (numRooms >= 50) {
	assignedTeam = "Large Bookings Team";
	teamEmail = "user@example.com";
	priority = "High";
} else if (numRooms >= 20) {
	assignedTeam = "Group Bookings Team";
	teamEmail = "user@example.com";
	priority = "Medium";
} else {
	assignedTeam = "Regular Bookings Team";
	teamEmail = "user@example.com";
	priority = "Normal";
}

// Rule 2: Urgency override
if (bookingData.urgency === "urgent") {
	priority = "High";
	assignedTeam = "Urgent Bookings Team";
	teamEmail = "user@example.com";
}

// Rule 3: Check-in date proximity
const checkInDate = new Date(bookingData.check_in_date);
const today = new Date();
const daysUntilCheckIn = Math.ceil((checkInDate - today) / (1000 * 60 * 60 * 24));

if (daysUntilCheckIn <= 7 && daysUntilCheckIn >= 0) {
	priority = "High";
	status = "Urgent - Near Check-in";
}

// Rule 4: VIP Agencies
const vipAgencies = ["Premium Travel Co", "Elite Bookings", "Luxury Tours"];
if (vipAgencies.some(agency => bookingData.travel_agency_name?.includes(agency))) {
	priority = "High";
	assignedTeam = "VIP Relations Team";
	teamEmail = "user@example.com";
}

// Calculate total nights
const checkOutDate = new Date(bookingData.check_out_date);
const totalNights = Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24));

// Get original email info - FIXED LINE BELOW
const originalEmail = $('Look for incoming emails').item.json;

return {
	json: {
		// Extracted booking data
		travel_agency_name: bookingData.travel_agency_name || "N/A",
		contact_person: bookingData.contact_person || "N/A",
		contact_email: bookingData.contact_email || "N/A",
		contact_phone: bookingData.contact_phone || "N/A",
		number_of_rooms: numRooms,
		check_in_date: bookingData.check_in_date || "N/A",
		check_out_date: bookingData.check_out_date || "N/A",
		total_nights: totalNights || 0,
		room_type: bookingData.room_type || "Standard",
		special_requests: bookingData.special_requests || "None",
		total_guests: bookingData.total_guests || numRooms * 2,
		booking_reference: bookingData.booking_reference || `BK${Date.now()}`,
		urgency: bookingData.urgency || "normal",

		// Business rules output
		assigned_team: assignedTeam,
		team_email: teamEmail,
		priority: priority,
		status: status,
		days_until_checkin: daysUntilCheckIn,

		// Metadata
		processed_date: new Date().toISOString(),
		original_subject: originalEmail.subject,
		original_sender: originalEmail.from,
		case_id: `CASE-${Date.now()}`,
		processing_status: "SUCCESS"
	}
};
