// Validate incoming data
const item = $input.first().json;

// Required fields validation
const requiredFields = ['clientName', 'clientEmail', 'clientCompany', 'projectTitle'];
const missingFields = requiredFields.filter(field => !item[field] || item[field].trim() === '');

// Validate items array
if (!item.items || !Array.isArray(item.items) || item.items.length === 0) {
  missingFields.push('items (must be array with at least one item)');
}

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(item.clientEmail)) {
  throw new Error('Invalid email format for clientEmail');
}

// Validate and calculate items
for (let i = 0; i < item.items.length; i++) {
  const lineItem = item.items[i];
  if (!lineItem.description || !lineItem.quantity || !lineItem.rate) {
    throw new Error(`Item ${i + 1} missing required fields (description, quantity, rate)`);
  }
  item.items[i].amount = lineItem.amount || (lineItem.quantity * lineItem.rate);
}

// Calculate totals
item.subtotal = item.subtotal || item.items.reduce((sum, lineItem) => sum + lineItem.amount, 0);
item.discount = item.discount || 0;
item.tax = item.tax || 0;
item.total = item.total || (item.subtotal - item.discount + item.tax);

// Generate proposal number if not provided
if (!item.proposalNumber) {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
  item.proposalNumber = `PROP-${year}${month}-${random}`;
}

// Add defaults
item.currency = item.currency || 'USD';
item.currencySymbol = item.currencySymbol || '$';
item.proposalDate = item.proposalDate || new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });
item.validUntil = item.validUntil || new Date(Date.now() + 30*24*60*60*1000).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

return { json: item };

// Add company branding and additional details
const item = $input.first().json;

// Company information (customize these)
const companyDefaults = {
  companyName: item.companyName || 'Media Jade',
  companyAddress: item.companyAddress || '456 Company Street',
  companyCity: item.companyCity || 'San Francisco, CA 94105',
  companyEmail: item.companyEmail || 'user@example.com',
  companyPhone: item.companyPhone || '+1234567890',
  companyWebsite: item.companyWebsite || 'www.mediajde.com',
  companyLogo: item.companyLogo || '',

  // Proposal terms
  paymentTerms: item.paymentTerms || '50% upfront, 50% upon completion',
  deliveryTimeline: item.deliveryTimeline || '4-6 weeks from project kickoff',
  terms: item.terms || 'This proposal is valid for 30 days. Upon acceptance, a detailed contract will be provided. All work will be completed according to agreed specifications and timelines.',

  // Client defaults
  clientPhone: item.clientPhone || '',
  clientAddress: item.clientAddress || '',
  clientCity: item.clientCity || ''
};

return { json: { ...item, ...companyDefaults } };

// Generate professional HTML proposal
const item = $input.first().json;

const html = `
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Proposal - ${item.proposalNumber}</title>
    <style>
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body { font-family: 'Inter', sans-serif; line-height: 1.6; color: #1f2937; }
        .container { max-width: 800px; margin: 0 auto; padding: 50px 40px; }
        .header { display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 50px; padding-bottom: 30px; border-bottom: 4px solid #3b82f6; }
        .logo { max-width: 180px; height: auto; }
        .company-name { color: #3b82f6; font-size: 28px; font-weight: 700; margin-bottom: 10px; }
        .company-details { font-size: 13px; color: #6b7280; line-height: 1.8; }
        .proposal-badge { text-align: right; }
        .proposal-title { background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%); color: white; padding: 15px 30px; border-radius: 8px; font-size: 24px; font-weight: 700; letter-spacing: 2px; margin-bottom: 15px; box-shadow: 0 4px 6px rgba(59, 130, 246, 0.3); }
        .proposal-meta { font-size: 13px; color: #6b7280; text-align: right; }
        .info-grid { display: grid; grid-template-columns: repeat(2, 1fr); gap: 25px; margin-bottom: 50px; }
        .info-card { background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%); padding: 25px; border-radius: 12px; border: 1px solid #e2e8f0; }
        .info-card h3 { color: #3b82f6; font-size: 14px; font-weight: 700; text-transform: uppercase; letter-spacing: 1.5px; margin-bottom: 15px; padding-bottom: 10px; border-bottom: 2px solid #3b82f6; }
        .info-card p { font-size: 14px; color: #4b5563; margin: 8px 0; }
        .info-card .name { font-size: 18px; font-weight: 600; color: #1f2937; margin-bottom: 5px; }
        .section { margin-bottom: 45px; }
        .section-title { color: #1e40af; font-size: 22px; font-weight: 700; margin-bottom: 20px; padding-bottom: 12px; border-bottom: 3px solid #dbeafe; }
        .section-content { color: #4b5563; font-size: 15px; line-height: 1.9; }
        .items-table { width: 100%; border-collapse: separate; border-spacing: 0; margin-bottom: 30px; box-shadow: 0 4px 6px rgba(0, 0, 0, 0.07); border-radius: 12px; overflow: hidden; }
        .items-table thead { background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%); }
        .items-table th { padding: 16px; text-align: left; font-weight: 600; font-size: 14px; color: white; text-transform: uppercase; }
        .items-table tbody tr { background: white; }
        .items-table tbody tr:nth-child(even) { background: #f9fafb; }
        .items-table tbody tr:hover { background: #eff6ff; }
        .items-table td { padding: 16px; border-bottom: 1px solid #e5e7eb; font-size: 14px; color: #374151; }
        .items-table tbody tr:last-child td { border-bottom: none; }
        .text-right { text-align: right; }
        .pricing-summary { margin-left: auto; width: 400px; background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%); padding: 25px; border-radius: 12px; border: 2px solid #e2e8f0; }
        .pricing-row { display: flex; justify-content: space-between; padding: 12px 0; font-size: 15px; color: #4b5563; }
        .pricing-row.total { border-top: 3px solid #3b82f6; margin-top: 15px; padding-top: 20px; font-weight: 700; font-size: 20px; color: #1e40af; }
        .terms-section { margin-top: 50px; padding: 30px; background: linear-gradient(135deg, #fef3c7 0%, #fde68a 100%); border-left: 6px solid #f59e0b; border-radius: 8px; }
        .terms-section h3 { color: #92400e; font-size: 18px; font-weight: 700; margin-bottom: 20px; }
        .term-item { background: rgba(255, 255, 255, 0.6); padding: 15px; border-radius: 6px; margin-bottom: 10px; }
        .term-item strong { color: #78350f; display: block; margin-bottom: 5px; font-size: 14px; }
        .term-item p { color: #92400e; font-size: 14px; margin: 0; }
        .footer { margin-top: 60px; padding-top: 30px; border-top: 3px solid #e5e7eb; text-align: center; }
        .footer-content { background: linear-gradient(135deg, #eff6ff 0%, #dbeafe 100%); padding: 30px; border-radius: 12px; }
        .footer h4 { color: #1e40af; font-size: 20px; margin-bottom: 15px; }
        .footer p { color: #4b5563; font-size: 14px; line-height: 1.8; margin: 8px 0; }
        .footer .contact { color: #3b82f6; font-weight: 600; }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <div>
                ${item.companyLogo ? `<img src="${item.companyLogo}" alt="${item.companyName}" class="logo">` : `<h1 class="company-name">${item.companyName}</h1>`}
                <div class="company-details">
                    ${item.companyAddress}<br>
                    ${item.companyCity}<br>
                    ${item.companyEmail}<br>
                    ${item.companyPhone}
                </div>
            </div>
            <div class="proposal-badge">
                <div class="proposal-title">PROPOSAL</div>
                <div class="proposal-meta"><strong>Date:</strong> ${item.proposalDate}</div>
            </div>
        </div>

        <div class="info-grid">
            <div class="info-card">
                <h3>Prepared For</h3>
                <p class="name">${item.clientName}</p>
                <p><strong>${item.clientCompany}</strong></p>
                <p>${item.clientEmail}</p>
                ${item.clientPhone ? `<p>${item.clientPhone}</p>` : ''}
                ${item.clientAddress ? `<p>${item.clientAddress}</p>` : ''}
            </div>
            <div class="info-card">
                <h3>Proposal Details</h3>
                <p><strong>Proposal #:</strong> ${item.proposalNumber}</p>
                <p><strong>Date:</strong> ${item.proposalDate}</p>
                <p><strong>Valid Until:</strong> ${item.validUntil}</p>
            </div>
        </div>

        <div class="section">
            <h2 class="section-title">${item.projectTitle}</h2>
            <div class="section-content">${item.projectDescription || 'Professional services as discussed.'}</div>
        </div>

        <div class="section">
            <h2 class="section-title">Investment Breakdown</h2>
            <table class="items-table">
                <thead>
                    <tr>
                        <th style="width: 50%;">Description</th>
                        <th class="text-right" style="width: 15%;">Quantity</th>
                        <th class="text-right" style="width: 17%;">Rate</th>
                        <th class="text-right" style="width: 18%;">Amount</th>
                    </tr>
                </thead>
                <tbody>
                    ${item.items.map(lineItem => `
                    <tr>
                        <td>${lineItem.description}</td>
                        <td class="text-right">${lineItem.quantity}</td>
                        <td class="text-right">${item.currencySymbol}${lineItem.rate.toFixed(2)}</td>
                        <td class="text-right"><strong>${item.currencySymbol}${lineItem.amount.toFixed(2)}</strong></td>
                    </tr>
                    `).join('')}
                </tbody>
            </table>

            <div class="pricing-summary">
                <div class="pricing-row">
                    <span>Subtotal:</span>
                    <span>${item.currencySymbol}${item.subtotal.toFixed(2)}</span>
                </div>
                ${item.discount > 0 ? `<div class="pricing-row"><span>Discount:</span><span>-${item.currencySymbol}${item.discount.toFixed(2)}</span></div>` : ''}
                ${item.tax > 0 ? `<div class="pricing-row"><span>Tax:</span><span>${item.currencySymbol}${item.tax.toFixed(2)}</span></div>` : ''}
                <div class="pricing-row total">
                    <span>Total Investment:</span>
                    <span>${item.currencySymbol}${item.total.toFixed(2)}</span>
                </div>
            </div>
        </div>

        <div class="terms-section">
            <h3>Terms & Conditions</h3>
            <div class="term-item">
                <strong>Payment Terms:</strong>
                <p>${item.paymentTerms}</p>
            </div>
            <div class="term-item">
                <strong>Delivery Timeline:</strong>
                <p>${item.deliveryTimeline}</p>
            </div>
            <div class="term-item">
                <strong>Additional Terms:</strong>
                <p>${item.terms}</p>
            </div>
        </div>

        <div class="footer">
            <div class="footer-content">
                <h4>Thank You for Considering Our Proposal!</h4>
                <p>For questions, please contact us:</p>
                <p class="contact">${item.companyEmail} | ${item.companyPhone}</p>
                <p style="margin-top: 15px; font-size: 13px; color: #6b7280;">Valid until ${item.validUntil}</p>
            </div>
        </div>
    </div>
</body>
</html>
`;

return {
  json: {
    html: html,
    proposalNumber: item.proposalNumber,
    clientName: item.clientName,
    clientEmail: item.clientEmail,
    clientCompany: item.clientCompany,
    total: item.total,
    currency: item.currency,
    fileName: `Proposal_${item.proposalNumber}_${item.clientCompany.replace(/[^a-zA-Z0-9]/g, '_')}.pdf`,
    ...item
  }
};

