// Validate incoming statement data
const item = $input.first().json;

// Required fields validation
const requiredFields = ['customerName', 'customerEmail', 'accountNumber', 'statementPeriod', 'transactions'];
const missingFields = requiredFields.filter(field => !item[field] || (typeof item[field] === 'string' && item[field].trim() === ''));

// Validate transactions array
if (!item.transactions || !Array.isArray(item.transactions) || item.transactions.length === 0) {
  missingFields.push('transactions (must be array with at least one transaction)');
}

if (missingFields.length > 0) {
  throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(item.customerEmail)) {
  throw new Error('Invalid email format for customerEmail');
}

// Validate statement period
if (!item.statementPeriod.startDate || !item.statementPeriod.endDate) {
  throw new Error('Statement period must include startDate and endDate');
}

// Validate and process transactions
for (let i = 0; i < item.transactions.length; i++) {
  const txn = item.transactions[i];
  if (!txn.date || !txn.description) {
    throw new Error(`Transaction ${i + 1} missing required fields (date, description)`);
  }

  // Ensure numeric values
  txn.debitAmount = parseFloat(txn.debitAmount) || 0;
  txn.creditAmount = parseFloat(txn.creditAmount) || 0;

  // Parse transaction date
  txn.dateObj = new Date(txn.date);
}

// Sort transactions by date (oldest first)
item.transactions.sort((a, b) => a.dateObj - b.dateObj);

// Calculate running balance
let runningBalance = parseFloat(item.openingBalance) || 0;
for (let i = 0; i < item.transactions.length; i++) {
  const txn = item.transactions[i];
  runningBalance += txn.debitAmount - txn.creditAmount;
  item.transactions[i].balance = runningBalance;
}

// Calculate totals
item.totalDebits = item.transactions.reduce((sum, txn) => sum + txn.debitAmount, 0);
item.totalCredits = item.transactions.reduce((sum, txn) => sum + txn.creditAmount, 0);
item.currentBalance = runningBalance;
item.openingBalance = parseFloat(item.openingBalance) || 0;

// Calculate aging analysis
const today = new Date();
const aging = {
  current: 0,
  days30: 0,
  days60: 0,
  days90plus: 0
};

// Group outstanding invoices by age
const outstandingInvoices = item.transactions.filter(txn => 
  txn.debitAmount > 0 && txn.type === 'invoice' && !txn.paid
);

outstandingInvoices.forEach(invoice => {
  const invoiceDate = new Date(invoice.date);
  const daysOld = Math.floor((today - invoiceDate) / (1000 * 60 * 60 * 24));

  if (daysOld <= 30) {
    aging.current += invoice.debitAmount;
  } else if (daysOld <= 60) {
    aging.days30 += invoice.debitAmount;
  } else if (daysOld <= 90) {
    aging.days60 += invoice.debitAmount;
  } else {
    aging.days90plus += invoice.debitAmount;
  }
});

item.agingAnalysis = aging;
item.totalOutstanding = aging.current + aging.days30 + aging.days60 + aging.days90plus;

// Determine if account is overdue
item.isOverdue = aging.days30 + aging.days60 + aging.days90plus > 0;
item.overdueAmount = aging.days30 + aging.days60 + aging.days90plus;

// Set currency defaults
item.currency = item.currency || 'USD';
item.currencySymbol = item.currencySymbol || '$';

// Generate statement number
if (!item.statementNumber) {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  item.statementNumber = `STMT-${year}${month}-${item.accountNumber}`;
}

// Format dates
const formatDate = (dateStr) => {
  return new Date(dateStr).toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' });
};

item.statementPeriod.startDateFormatted = formatDate(item.statementPeriod.startDate);
item.statementPeriod.endDateFormatted = formatDate(item.statementPeriod.endDate);
item.statementDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

return { json: item };

// Add company information and branding
