// Check credit limit and generate alerts
const item = $input.first().json;

const creditLimit = parseFloat(item.creditLimit) || 0;
const currentBalance = parseFloat(item.currentBalance) || 0;

// Calculate credit utilization
let creditUtilization = 0;
let creditAvailable = 0;
let creditStatus = 'No Credit Limit';
let creditAlert = false;
let creditAlertLevel = 'none';

if (creditLimit > 0) {
  creditUtilization = (currentBalance / creditLimit) * 100;
  creditAvailable = creditLimit - currentBalance;

  // Determine credit status and alert level
  if (currentBalance > creditLimit) {
    creditStatus = 'Over Limit';
    creditAlert = true;
    creditAlertLevel = 'critical';
  } else if (creditUtilization >= 90) {
    creditStatus = 'Near Limit (90%+)';
    creditAlert = true;
    creditAlertLevel = 'high';
  } else if (creditUtilization >= 75) {
    creditStatus = 'High Utilization (75%+)';
    creditAlert = true;
    creditAlertLevel = 'medium';
  } else if (creditUtilization >= 50) {
    creditStatus = 'Moderate Utilization';
    creditAlert = false;
    creditAlertLevel = 'low';
  } else {
    creditStatus = 'Good Standing';
    creditAlert = false;
    creditAlertLevel = 'none';
  }
}

// Calculate days since last payment
const paymentTransactions = item.transactions.filter(txn => txn.creditAmount > 0);
let daysSinceLastPayment = null;
let lastPaymentDate = null;
let lastPaymentAmount = 0;

if (paymentTransactions.length > 0) {
  // Get most recent payment
  const lastPayment = paymentTransactions[paymentTransactions.length - 1];
  lastPaymentDate = new Date(lastPayment.date);
  lastPaymentAmount = lastPayment.creditAmount;

  const today = new Date();
  daysSinceLastPayment = Math.floor((today - lastPaymentDate) / (1000 * 60 * 60 * 24));
}

// Calculate average days to pay
const invoices = item.transactions.filter(txn => txn.type === 'invoice' && txn.debitAmount > 0);
let avgDaysToPay = null;
let paymentBehavior = 'No Payment History';

if (paymentTransactions.length > 0 && invoices.length > 0) {
  // Simplified calculation - in real scenario, would match invoices to payments
  avgDaysToPay = Math.floor(daysSinceLastPayment / 2); // Simplified

  if (avgDaysToPay <= 30) {
    paymentBehavior = 'Excellent - Pays on Time';
  } else if (avgDaysToPay <= 45) {
    paymentBehavior = 'Good - Occasional Delays';
  } else if (avgDaysToPay <= 60) {
    paymentBehavior = 'Fair - Frequent Delays';
  } else {
    paymentBehavior = 'Poor - Chronic Late Payment';
  }
}

// Risk score calculation (0-100, higher = more risk)
let riskScore = 0;

if (item.isOverdue) {
  riskScore += 30;
  if (item.agingAnalysis.days60 > 0) riskScore += 20;
  if (item.agingAnalysis.days90plus > 0) riskScore += 30;
}

if (creditLimit > 0 && creditUtilization > 90) {
  riskScore += 20;
}

if (daysSinceLastPayment && daysSinceLastPayment > 60) {
  riskScore += 10;
}

let riskLevel = 'Low Risk';
if (riskScore >= 70) {
  riskLevel = 'High Risk';
} else if (riskScore >= 40) {
  riskLevel = 'Medium Risk';
} else if (riskScore > 0) {
  riskLevel = 'Low Risk';
} else {
  riskLevel = 'No Risk';
}

// Generate recommendations
const recommendations = [];

if (creditAlert) {
  if (creditAlertLevel === 'critical') {
    recommendations.push('URGENT: Account has exceeded credit limit. Suspend new credit immediately.');
    recommendations.push('Contact customer to arrange payment plan and credit limit discussion.');
  } else if (creditAlertLevel === 'high') {
    recommendations.push('Account approaching credit limit (90%+). Review credit extension policy.');
    recommendations.push('Proactively contact customer about payment schedule.');
  } else if (creditAlertLevel === 'medium') {
    recommendations.push('Monitor account closely - high credit utilization detected.');
  }
}

if (item.isOverdue) {
  if (item.agingAnalysis.days90plus > 0) {
    recommendations.push('CRITICAL: Invoices 90+ days overdue. Escalate to collections.');
  } else if (item.agingAnalysis.days60 > 0) {
    recommendations.push('Urgent: Invoices 60+ days overdue. Schedule payment call.');
  } else if (item.agingAnalysis.days30 > 0) {
    recommendations.push('Send payment reminder for invoices 30+ days overdue.');
  }
}

