// n8n Code node ‚Äî Simple Combine to One
// Combine multiple extractor results into one object without complex heuristics.

const items = $input.all();

// --- CONFIG ---
const AS_ARRAYS = true;   // true: save as array; false: join string " | "
const JOINER = " | ";     // separator when AS_ARRAYS=false

// Fields processed as "list" (will be merged & deduplicated)
const LIST_FIELDS = new Set([
  "keyPoints",        // array
  "relationType",     // can appear in various forms ‚Üí make it a list
  "reason",           // reasons can be more than one
  "articleGoal",      // goals can vary between pages
  "excerpt",          // can save all excerpts (or take first in SCALAR_PREFERS_FIRST)
  "summary"           // can save all summaries (or take first in SCALAR_PREFERS_FIRST)
]);

// If you want some fields to remain scalar (take first), put them here:
const SCALAR_PREFERS_FIRST = new Set([
  "relatedToBrand"    // take the first non-empty one
]);

// Helper
const norm = (v) => (v == null ? "" : String(v).trim());
const toArr = (v) => Array.isArray(v) ? v : (v == null ? [] : [v]);
const dedupe = (arr) => {
  const seen = new Set(); const out = [];
  for (const x of arr) {
    const s = norm(x);
    const k = s.toLowerCase();
    if (s && !seen.has(k)) { seen.add(k); out.push(s); }
  }
  return out;
};

// Extract record from various forms
const extractRecord = (it) => {
  if (it?.json?.output && typeof it.json.output === "object") return it.json.output;
  if (it?.json && typeof it.json === "object") return it.json;
  return {};
};

// Collect all records
const records = items.map(extractRecord);

// Initial combination
const combined = {};
let anyRelatedTrue = false;

// Loop to combine
for (const rec of records) {
  for (const [k, v] of Object.entries(rec)) {
    if (k === "isRelated") {
      if (v === true || v === "true") anyRelatedTrue = true;
      continue;
    }

    // If field is set for scalar-first ‚Üí save only the first one
    if (SCALAR_PREFERS_FIRST.has(k)) {
      if (!combined[k]) {
        const val = Array.isArray(v) ? v.find(x => norm(x)) : v;
        if (norm(val)) combined[k] = norm(val);
      }
      continue;
    }

    // Otherwise, treat as list so all values are included
    const list = toArr(v);
    if (!combined[k]) combined[k] = [];
    combined[k].push(...list);
  }
}

// Dedupe for all fields that are lists
for (const k of Object.keys(combined)) {
  if (Array.isArray(combined[k]) || LIST_FIELDS.has(k)) {
    combined[k] = dedupe(toArr(combined[k]));
  }
}

// Set boolean isRelated
combined.isRelated = anyRelatedTrue;

// Final output:
// - If AS_ARRAYS=false ‚Üí join lists into string with JOINER
if (!AS_ARRAYS) {
  for (const [k, v] of Object.entries(combined)) {
    if (Array.isArray(v)) combined[k] = v.join(JOINER);
  }
}

// Clean empty fields
for (const k of Object.keys(combined)) {
  const v = combined[k];
  if (Array.isArray(v) && v.length === 0) delete combined[k];
  if (typeof v === "string" && !v.trim()) delete combined[k];
}

return [{ json: combined }];

// n8n Code Node ‚Äî Format per-section (strings) for Notion blocks UI
// Input: items[0].json.output
// Output: multiple string fields: h1, h2_xxx, p_xxx (each <= ~1900 chars)

const item = $input.item;
const d = item.json.output || item.json || {};

// Safe content limit per Notion block (~2000 chars, using 1900)
const LIMIT = 1900;

const clip = (s) => String(s ?? '').slice(0, LIMIT).trim();

// Array ‚Üí bullets in one paragraph (1 Notion block, multiline)
function bulletsParagraph(arr) {
  if (!Array.isArray(arr) || !arr.length) return '- (none)';
  const lines = arr
    .filter(x => x != null && String(x).trim() !== '')
    .map(x => `‚Ä¢ ${String(x).replace(/\s+/g, ' ').trim()}`);
  return clip(lines.join('\n'));
}

// Scalar ‚Üí single string
const scalar = (s) => {
  const v = (s == null || String(s).trim() === '') ? '- (none)' : String(s);
  return clip(v);
};

// Get company name for H1 (layered fallback)
const companyName =
  d.companyName ||
  d.company ||
  item.json.companyName ||
  item.json['Company Name'] ||
  'Unknown Company';

// Arrange all sections as separate strings
const output = {
  // Main heading = Company Name
  h1: scalar(companyName),

  // Company Description
  h2_companyDescription: 'Company Description',
  p_companyDescription: scalar(d.companyDescription),

  // ICP
  h2_icp: 'Ideal Customer Profile (ICP)',
  p_icp: bulletsParagraph(d.idealCustomerProfile),

  // Pain Points
  h2_painPoints: 'Pain Points',
  p_painPoints: bulletsParagraph(d.painPoints),

  // Value Proposition
  h2_valueProposition: 'Value Proposition',
  p_valueProposition: bulletsParagraph(d.valueProposition),

  // Proof Points
  h2_proofPoints: 'Proof Points',
  p_proofPoints: bulletsParagraph(d.proofPoints),

  // Reasons to Outreach
  h2_reasonsToOutreach: 'Reasons to Outreach',
  p_reasonsToOutreach: bulletsParagraph(d.reasonsToOutreach),

  // Outreach Channels
  h2_outreachChannels: 'Outreach Channels',
  p_outreachChannels: bulletsParagraph(d.outreachChannels),

  // Suggested Keywords
  h2_suggestedKeywords: 'Suggested Keywords',
  p_suggestedKeywords: bulletsParagraph(d.suggestedKeywords),

  // Brand Tone
  h2_brandTone: 'Brand Tone',
  p_brandTone: scalar(d.brandTone),
};

return [{ json: output }];

// n8n Code Node ‚Äî Simple HTML ‚Üí Clean Text Array
// Output format: [{ "data": "clean text" }]

const items = $input.all();

// Helper: remove all HTML tags, comments, and script/style
function cleanHTML(html = '') {
  return html
    .replace(/<!--[\s\S]*?-->/g, ' ')                     // comments
    .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, ' ') // script
    .replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, ' ')   // style
    .replace(/<noscript\b[^<]*(?:(?!<\/noscript>)<[^<]*)*<\/noscript>/gi, ' ')
    .replace(/<\/?(svg|math)[\s\S]*?<\/\1>/gi, ' ')
    .replace(/<[^>]+>/g, ' ') // remove all tags
    .replace(/&nbsp;/gi, ' ')
    .replace(/&amp;/gi, '&')
    .replace(/&lt;/gi, '<')
    .replace(/&gt;/gi, '>')
    .replace(/&quot;/gi, '"')
    .replace(/&#39;/gi, "'")
    .replace(/\s+/g, ' ')
    .trim();
}

// Get html from several possible keys
function pickHtml(json) {
  const keys = ['data', 'html', 'body', 'content', 'text'];
  for (const k of keys) {
    if (typeof json?.[k] === 'string' && json[k].trim()) return json[k];
  }
  if (typeof json?.output === 'object') {
    for (const k of keys) {
      if (typeof json.output[k] === 'string' && json.output[k].trim()) return json.output[k];
    }
  }
  return '';
}

return items.map((item) => {
  const raw = pickHtml(item.json) || '';
  const cleaned = cleanHTML(raw);
  return { json: { data: cleaned } };
});

// Get all items from previous node (each item = one SerpAPI result)
const items = $input.all();
const out = [];

for (const item of items) {
  const data = item.json || {};
  const results = Array.isArray(data.organic_results) ? data.organic_results : [];

  // Collect links + fallback from redirect_link?url=...
  const urls = results.map(r => {
    if (r.link) return r.link;
    if (r.redirect_link) {
      try {
        const u = new URL(r.redirect_link);
        const real = u.searchParams.get('url');
        if (real) return real;
      } catch (e) {}
    }
    return null;
  }).filter(Boolean);

  // Dedupe
  const unique = [...new Set(urls)];

  // Output one item per URL (include useful metadata)
  for (const r of results) {
    // Match parsed URL with r.link/redirect_link
    let resolved = r.link;
    if (!resolved && r.redirect_link) {
      try {
        const u = new URL(r.redirect_link);
        const real = u.searchParams.get('url');
        if (real) resolved = real;
      } catch (e) {}
    }
    if (resolved && unique.includes(resolved)) {
      out.push({
        json: {
          url: resolved,
          position: r.position ?? null,
          title: r.title ?? null,
          displayed_link: r.displayed_link ?? null,
          source: r.source ?? null,
          date: r.date ?? null
        }
      });
    }
  }
}

// If no organic_results, return empty info to keep flow running
return out.length ? out : [{ json: { url: null, note: 'No organic_results found' } }];
