const items = $input.all();
const alerts = [];

for (const item of items) {
  const data = item.json;
  const vehicleInfo = {
    vehicle_id: data.vehicle_id,
    driver: data.driver || 'Unknown',
    make: data.make || 'Unknown',
    model: data.model || 'Unknown',
    year: data.year,
    mileage: data.mileage,
    engine_temp: data.engine_temp,
    oil_pressure: data.oil_pressure,
    brake_pad_thickness: data.brake_pad_thickness,
    tire_pressure: data.tire_pressure,
    battery_voltage: data.battery_voltage,
    last_service_date: data.last_service_date,
    last_oil_change_mileage: data.last_oil_change_mileage,
    needs_oil_change: data.needs_oil_change,
    low_brake_pads: data.low_brake_pads,
    low_tire_pressure: data.low_tire_pressure,
    battery_weak: data.battery_weak,
    engine_overheating: data.engine_overheating,
    low_oil_pressure: data.low_oil_pressure,
    service_overdue: data.service_overdue
  };

  if (data.needs_oil_change || data.low_brake_pads || data.low_tire_pressure || data.battery_weak || data.engine_overheating || data.low_oil_pressure || data.service_overdue) {
    alerts.push({
      ...vehicleInfo,
      severity: data.engine_overheating || data.low_oil_pressure ? 'critical' : data.low_brake_pads ? 'high' : 'medium',
      timestamp: new Date().toISOString()
    });
  }
}

return alerts.map(alert => ({ json: alert }));

// Simulate fleet of 5 vehicles with different characteristics
const fleet = [
  {
    vehicle_id: 'CAR-2024-001',
    make: 'Toyota',
    model: 'Camry',
    year: 2022,
    driver: 'John Smith',
    base_mileage: 45000
  },
  {
    vehicle_id: 'TRUCK-2024-002',
    make: 'Ford',
    model: 'F-150',
    year: 2021,
    driver: 'Sarah Johnson',
    base_mileage: 62000
  },
  {
    vehicle_id: 'VAN-2024-003',
    make: 'Mercedes',
    model: 'Sprinter',
    year: 2023,
    driver: 'Mike Davis',
    base_mileage: 28000
  },
  {
    vehicle_id: 'CAR-2024-004',
    make: 'Honda',
    model: 'Accord',
    year: 2020,
    driver: 'Emily Brown',
    base_mileage: 78000
  },
  {
    vehicle_id: 'SUV-2024-005',
    make: 'Chevrolet',
    model: 'Tahoe',
    year: 2022,
    driver: 'Robert Wilson',
    base_mileage: 51000
  }
];

return fleet.map(vehicle => ({
  json: {
    ...vehicle,
    mileage: vehicle.base_mileage + Math.floor(Math.random() * 5000),
    engine_temp: Math.floor(Math.random() * 50) + 80,
    oil_pressure: Math.floor(Math.random() * 30) + 25,
    brake_pad_thickness: Math.floor(Math.random() * 10) + 2,
    tire_pressure: Math.floor(Math.random() * 10) + 28,
    battery_voltage: parseFloat((Math.random() * 2 + 12).toFixed(1)),
    last_service_date: new Date(Date.now() - Math.floor(Math.random() * 180) * 24 * 60 * 60 * 1000).toISOString(),
    last_oil_change_mileage: Math.floor(Math.random() * 8000) + 1000
  }
}));

const items = $input.all();
const alerts = items.map(item => item.json);

// Group by severity
const critical = alerts.filter(a => a.severity === 'critical');
const high = alerts.filter(a => a.severity === 'high');
const medium = alerts.filter(a => a.severity === 'medium');
const low = alerts.filter(a => a.severity === 'low');

// Group by vehicle
const byVehicle = {};
alerts.forEach(alert => {
  const vid = alert.vehicle_id;
  if (!byVehicle[vid]) {
    byVehicle[vid] = {
      vehicle_id: vid,
      driver: alert.driver,
      make: alert.make,
      model: alert.model,
      alerts: []
    };
  }
  byVehicle[vid].alerts.push({
    type: alert.type,
    severity: alert.severity,
    message: alert.message
  });
});

const summary = {
  total_alerts: alerts.length,
  critical_count: critical.length,
  high_count: high.length,
  medium_count: medium.length,
  low_count: low.length,
  vehicles_with_alerts: Object.keys(byVehicle).length,
  timestamp: new Date().toISOString(),
  by_vehicle: Object.values(byVehicle),
  critical_vehicles: critical.map(a => `${a.vehicle_id} (${a.driver})`).join(', ') || 'None'
};

return [{ json: summary }];

