const items = $input.all();
const results = [];

for (const item of items) {
  const aiAnalysis = item.json.output || item.json.text || '';
  const originalData = item.json;

  results.push({
    vehicle_id: originalData.vehicle_id,
    driver: originalData.driver,
    make: originalData.make,
    model: originalData.model,
    mileage: originalData.mileage,
    ai_analysis: aiAnalysis,
    ai_analyzed_at: new Date().toISOString(),
    severity: originalData.engine_overheating || originalData.low_oil_pressure ? 'critical' : 
              originalData.low_brake_pads ? 'high' : 
              originalData.needs_oil_change || originalData.low_tire_pressure || originalData.battery_weak ? 'medium' : 'low',
    raw_data: {
      engine_temp: originalData.engine_temp,
      oil_pressure: originalData.oil_pressure,
      brake_pad_thickness: originalData.brake_pad_thickness,
      tire_pressure: originalData.tire_pressure,
      battery_voltage: originalData.battery_voltage
    }
  });
}

return results.map(r => ({ json: r }));
